INSERT INTO `npc_text_localized` VALUES ('11295', 'deDE', 'Dieser Typ ist gar nichts, höchstens ein Narr!$B$BRuhm, Reichtümer? Quatsch! An diesem Ort findet man nur eins, den Tod, Mann!$B$BIch hab\' in meinem Leben schon so einige Trolle gesehen, aber die hier', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
