INSERT INTO `npc_text_localized` VALUES ('11299', 'deDE', '<Budd verdreht die Augen als Ihr Euch ihm nähert.>$B$BAhh, ja, die Schon-fast-Berühmtheit $N!$B$BWarum beschert Ihr mir diese Ehre?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11302', 'deDE', 'Na, na, na, $N, jetzt beruhigt Euch erst einmal. Ihr müsst wirklich lernen, mir zu vertrauen.$B$BEure mangelnde Erfahrung in diesen Situationen lässt Euch rasch in Panik verfallen. Wer in Panik gerät,', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11309', 'deDE', 'Falls Ihr mit Lehrling Garion sprechen wollt, er hält sich innerhalb der Hütte mit Tabetha auf. Zumindest kann er dort nicht alle Reagenzien stehlen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11311', 'deDE', 'Ich wünschte, es gäbe einen Priester oder Druiden hier draußen. Solange meine Verletzungen nicht geheilt sind, kann ich meine Studien nicht fortführen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11313', 'deDE', 'Wartet mal, jetzt sagt nicht... $N, richtig?$B$BWas kann ich für Euch tun?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11314', 'deDE', 'Oh, naja, das kommt ganz darauf an. Viele von ihnen sind über die Jahre gekommen und wieder gegangen - wollten sich stets am Ruhm des legendären Budd Winterhäldler laben!$B$BIhr könnt mir glauben, die', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11315', 'deDE', 'Hört mal zu, $N, Ihr solltet mittlerweile wissen, dass man das Glück beim Schopfe packt, wenn es einem über den Weg läuft. Fragen stellt man später. Chancen wie diese bekommt man nicht alle Tage!$B$BA', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11316', 'deDE', 'Ahh, die - das ist eine traurige Geschichte.$B$BSie verschwanden hinter den Mauern Zul\'Amans. Ich habe seit Tagen nichts mehr von ihnen gehört.$B$BJa, ja, dieses übereifrige Verlangen nach Ruhm und Re', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
