INSERT INTO `npc_text_localized` VALUES ('11956', 'deDE', 'In dunklen Zeiten schließt man seltsame Freundschaften. Vielleicht lohnt sich ein Besuch im alten Lordaeron ja, um zu sehen, wie die Verlassenen ihren kleinen Weidenmann verbrennen.', 'In dunklen Zeiten schließt man seltsame Freundschaften. Vielleicht lohnt sich ein Besuch im alten Lordaeron ja, um zu sehen, wie die Verlassenen ihren kleinen Weidenmann verbrennen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
