INSERT INTO `npc_text_localized` VALUES ('12097', 'deDE', 'Wir haben diesen Ort mit bloßen Händen erbaut! Blut und Tränen, mein Freund.$B$BGlaubt mir, was ich sage: Wir haben nicht vor, Valgarde jemals aufzugeben.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
