INSERT INTO `npc_text_localized` VALUES ('12100', 'deDE', 'Ich habe Euch vorhin schon gesehen und gehofft, dass Ihr hier vorbeischauen würdet. Was kann ich für Euch tun, Schnuckelchen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
