INSERT INTO `npc_text_localized` VALUES ('12135', 'deDE', 'Seid kein Dummkopf, Fremder, geht wieder dorthin zurück, woher Ihr gekommen seid.$B$BDies ist kein Ort für unbekümmerte Abenteurer. Man frisst Euresgleichen hier auf, und was übrig bleibt, sind die Kn', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
