INSERT INTO `npc_text_localized` VALUES ('12159', 'deDE', 'Alle lieben die verrückten Rennwidder des Braufests. Wollt Ihr mehr darüber wissen? Sprecht mit Driz Purzelfix unten beim Widderstall. Viel Vergnügen und verdient Euch dabei am besten ein paar Braufes', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12160', 'deDE', 'Hey, hey, vergesst nicht, ein paar Souvenirs des Braufests mitzunehmen! Wir haben alles, von traditionellen Trachten bis hin zu Miniaturfässchen mit leckerem Gebräu. Sprecht einfach mit Blix Fixtand. ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
