INSERT INTO `npc_text_localized` VALUES ('12163', 'deDE', '', 'Ich werde es Euch gerne vorlesen!$B$BWeint nicht an diesem Unglücksort,$B$Bich schlafe nicht, ich bin nicht dort.$B$BIch bin der Schnee und der Wind, der fegt,$B$Bsich über Nordends Weiten legt.$B$BIc', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12165', 'deDE', 'Ich sag\'s Euch, nur sturzbetrunken könnt Ihr Euch auf die Jagd begeben!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
