INSERT INTO `npc_text_localized` VALUES ('12170', 'deDE', 'Besser ist es, den Kopf unten zu halten, \"Kumpel\"! Wennde weißt, was ich mein!$B$BJarrrr!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
