INSERT INTO `npc_text_localized` VALUES ('12174', 'deDE', 'Blutelfenschätze sind ohnegleichen. Es liegt nicht nur am Gold oder an der Seide oder an den Edelsteinen, die als Materialien verwendet werden. Die Handwerkskunst der Sin\'dorei ist wertvoll an sich.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
