INSERT INTO `npc_text_localized` VALUES ('12176', 'deDE', '', 'Tagein tagaus will der verrückte Jonah Sterling, dass ich das Deck schrubbe.$B$BDas Deck schrubben?!$B$BIch bin Magier und schrubbe GAR NICHTS! Zumindest nicht auf gewöhnliche Weise.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
