INSERT INTO `npc_text_localized` VALUES ('12186', 'deDE', '', 'Der verfluchte Jonah Sterling wird uns direkt ins Verderben führen. Würde er mehr Zeit außerhalb seiner Höhle verbringen, dann wüsste er, wie töricht es ist, in Kriegszeiten eine erfahrene Flotte anzu', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12188', 'deDE', 'Viele Veränderungen beeinflussen das Land und das Wasser: Ihr, sie... viele Veränderungen.$B$BDie Lebensweise in Kamagua wird von diesen Veränderungen bedroht. Was werdet Ihr tun?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12189', 'deDE', 'Ich bin nicht in meinem Element, was? Walt weiß nicht, wovon er redet! Ich hab mit verbundenen Augen das Eisen aus diesen Zwergen geprügelt und ein Lager aufgeschlagen, um die Vorgänge an der Ausgrabu', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12190', 'deDE', 'Der Dude ist dort drüben und ziemlich angekratzt wegen seines Teppichs.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12191', 'deDE', 'N\'abend Chef.', 'N\'abend Chef.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12193', 'deDE', 'Ich glaur... glaur... glaurbe, dass Ihr zurück zum Außenposten wollt. Jarp?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12194', 'deDE', 'Walts Werkbank ist einsatzbereit.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12195', 'deDE', 'Zweimal täglich, jeweils morgens und abends um viertel nach sechs Uhr, kommt Hochtüftler Mekkadrill für die Zeremonie des Fassanstichs höchstpersönlich zum Braufestplatz. Lasst es Euch nicht entgehen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12196', 'deDE', 'Zweimal täglich, jeweils morgens und abends um viertel nach sechs, kommt Schattenjäger Vol\'jin für die Zeremonie des Fassanstichs höchstpersönlich zum Braufestplatz. Ihr wärt ganz schön dumm, wenn Ihr', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12207', 'deDE', 'Ein Hoch auf Käpt\'n Torgoley!', '', 'Ich schrubb\' die Decks!', '', 'Ein Hoch auf unsere Lady Mehley - das beste Schiff aller Zeiten!', '', 'Deck-Schrrrubben is\' gesund!', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12208', 'deDE', 'Die Zeiten ändern sich, $N. Krieg ist ausgebrochen.$B$BWird unser Volk der Lage gewachsen sein, und unsere Verbündeten unterstützen... oder werden wir untätig verweilen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12209', 'deDE', 'Entschuldigung, aber ich habe keine Zeit, um mich zu unterhalten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12210', 'deDE', '', 'Hey, $GBürschchen:Mädel;, seht Ihr nich\' dass ich hier am Arbeiten bin? Verschwindet, bevor Kowalski mich plappern sieht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12212', 'deDE', 'Torgoley nimmt uns hart ran. Aber ein guter Kapitän, das ist er allemal.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12213', 'deDE', 'Es sind nicht viele von uns übrig, $N. Die Rückkehr unseres alten Feindes ist ein Grund für große Besorgnis.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12214', 'deDE', 'Ich muss auf mein Schiff und meine Mannschaft Acht geben, $Gverehrter Herr:verehrte Dame;. Verzeiht, dass ich keine Zeit für ein Gespräch mit Euch aufbringen kann.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12215', 'deDE', 'Die Lady Mehley ist schon so lange im Hafen von Theramore, dass wir mit dem Schutz ihrer Passagiere beauftragt wurden.', 'Die Lady Mehley ist schon so lange im Hafen von Theramore, dass wir mit dem Schutz ihrer Passagiere beauftragt wurden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12216', 'deDE', 'Willkommen auf der majestätischen Lady Mehley. Ich bin Kapitän Torgoley.$B$BSolltet Ihr Fragen bezüglich des Schiffs haben, dann könnt Ihr diese gerne an mich richten. Ansonsten wünsche ich eine angen', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12218', 'deDE', 'Sprecht mit Smutje Gunzl, falls Ihr Verpflegung benötigen solltet.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12219', 'deDE', 'Solltet Ihr nach Waren und Ausrüstung suchen, dann sprecht mit Kerbe; er ist der Ingenieur und Rüstmeister unseres Schiffs. Außerdem kann er beschädigte Ausrüstungsgegenstände reparieren.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12220', 'deDE', 'Ach ja, das... der Name. Nun... es ist so wie man sagt: Ein guter Name oder Wille wird hart erarbeitet, aber leicht verloren. Wir wollen nicht ins Detail gehen - dass manche mich so nennen, sollte gen', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12221', 'deDE', 'Die Toten erheben sich und dürsten nach Rache an jedem, der es wagt, ihr Gebiet zu betreten. Dreht um!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12222', 'deDE', 'Ich hab\' Schwein gehabt. Nicht viele Tauren bekommen die Gelegenheit, Kabinenjunge auf einer Fregatte wie dieser zu werden. Grezzix lässt mich sogar diese unglaublichen Schlachtkanus bemannen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12226', 'deDE', 'Wenn unsere Erschaffung eines Portals zur Insel Quel\'Danas - und dem Sonnenbrunnen darauf - Erfolg haben soll, dann müssen wir eine Energiequelle finden, die mächtig genug ist, um das Portal zu öffnen', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12227', 'deDE', 'Dieses Portal ist nicht wie die anderen, die zurück nach Azeroth führen. Um es zu erhalten, müssen wir mehr Manazellen heranschaffen.$B$BEntweder das, oder das Risiko eingehen, unseren Zugang zur Inse', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12237', 'deDE', '', 'Grüße, $R.$B$BMein Laden ist endlich eröffnet. Ich hoffe, Euch gefällt, was Ihr seht!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12239', 'deDE', '', 'Das ist das Tor, das Kaels Armeen benutzt haben, um sich zum Sonnenbrunnen zurückzuziehen. Einige seiner Schoßhunde sind hier zurückgeblieben; die Legion hat sie auf eine höchst... ungewöhnliche Weise', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12240', 'deDE', '', 'Habt Ihr die beschädigten Wachen gesehen, die auf der Insel herumstreifen? Einige Modifikationen an den Kristallen, die ihr Verhalten kontrollieren, werden uns Kontrolle über sie verschaffen und wir k', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12241', 'deDE', '', 'Die Übernahme des Sanktums war ein Erfolg. Wir können damit fortfahren, Wachposten zu konvertieren, um sie als Wachen an anderen taktisch wichtigen Standpunkten einzusetzen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12242', 'deDE', 'Ahoi! Ihr wollt also eine Fahrt mit der \"Gnädigen Schwester\" buchen, Kamerad? Ihr seid am richtigen Ort...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12243', 'deDE', 'Wir müssen dafür sorgen, dass wir nicht noch mehr Seelen an die See verlieren.', 'Wir müssen dafür sorgen, dass wir nicht noch mehr Seelen an die See verlieren.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12246', 'deDE', 'Die Irrwische und ich halten nach verlorenen Seelen Ausschau.', 'Die Irrwische und ich halten nach verlorenen Seelen Ausschau.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12247', 'deDE', 'Die Weiten der Meere sind gefährlich, $C. Wir müssen auf der Hut sein.', 'Die Weiten der Meere sind gefährlich, $C. Wir müssen auf der Hut sein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12248', 'deDE', 'Es tut mir leid, aber im Moment kann ich mich nicht mit Euch unterhalten.', 'Es tut mir leid, aber im Moment kann ich mich nicht mit Euch unterhalten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12251', 'deDE', 'Mögen die Naaru Euch segnen, mein Freund.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12252', 'deDE', 'In meinem Beruf bekommt man nicht viel von den Kämpfen zu sehen. So nah an der Frontlinie einer Gefechtszone zu dienen ist eine feine Abwechslung.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12255', 'deDE', 'Vom Sanktum der Sonnenweiten Besitz zu ergreifen, war der erste Schritt unseres Schlachtplanes. Unsere Befehle lauten, als nächstes die Kontrolle über die Waffenkammer zu übernehmen.$B$BWir haben unse', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12256', 'deDE', 'Wir haben die Kontrolle über die Waffenkammer erlangt, doch unsere Arbeit auf der Insel ist noch nicht abgeschlossen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12257', 'deDE', '', 'Meine Befehle lauten, an der Todesschneise für eine Ablenkung zu sorgen, sodass unsere Truppen die Waffenkammer der Sonnenweiten einnehmen können. Es ist vielleicht nicht so glamourös, wie von Angesic', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12258', 'deDE', '', 'Wir haben die Waffenkammer erobert, $N. Aber wir sollten uns nicht auf unseren Lorbeeren ausruhen. Die Legion wird es auch nicht tun.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12259', 'deDE', 'Unsere Mühen werden belohnt. Wir haben das Sanktum der Sonnenweiten erobert, $N. Geht dorthin und seht nach, ob Ihr jemandem helfen könnt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12260', 'deDE', 'Das Aktivieren der Barrieren des Sanktums wird uns dabei unterstützen, es schneller zu sichern. Wir wollen keine Getriebenen im Weg haben, wenn wir einen Großangriff auf die Legion und Kael starten.$B', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12262', 'deDE', 'Die Mannschaft der Bravado wird ihr Bestes tun, um Eure Reise so schnell und angenehm wie möglich von statten gehen zu lassen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12263', 'deDE', 'Entschuldigt bitte, wir müssen uns wohl ein anderes Mal unterhalten. Lebt wohl.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12266', 'deDE', '', 'Ich beneide Wellensang schon fast. Ach, er könnte fast schöner sein als ich!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12267', 'deDE', '', 'Unglücklicher Name für einen Seemann, Nich\' wahr?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12268', 'deDE', 'Dieser angegraute Seebär mag zwar der Älteste der ganzen Mannschaft sein, aber er ist auch zäher als der Rest! Eh? Eh? Arme wie ein Oger!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12270', 'deDE', '', 'Wir haben geschworen, stets unser Bestes zu geben, um die Fahrgäste der Bravado zu beschützen. Die Seestraße zwischen Auberdine und dem Hafen von Sturmwind muss sicher bleiben.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12271', 'deDE', 'Hey, Kindchen. Ihr seid an Bord der Launischen Minna. Kapitän Düsenzang der Name, Ihr habt die Ehre, auf dem Stolz der Schwarzmeerräuber mitzusegeln. Die Blutsegelbukaniere kommen uns nicht zu nahe, n', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12272', 'deDE', 'Jawohl, auf ihr steht Ihr gerade. Eine Pracht, nicht wahr? Der Baron hat mich vor einiger Zeit zum Kapitän ernannt. Sie gehörte ursprünglich nicht mir, ich habe sie also auch nicht getauft. Wenn Ihr m', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12273', 'deDE', 'Wenn Euer Magen knurrt, sprecht mit Smutje Stahlbauch. Nur zwischen Euch und mir... vielleicht solltet Ihr noch etwas warten, er hat seinen Namen nicht ohne Grund.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12274', 'deDE', 'Was ist ein Dampfdruckschiff schon ohne einen Ingenieur, hä? Drillspindel kann Euch mit allem, was Ihr braucht, aushelfen. Er ist auch geschickt, wenn es um rasche Reparaturen geht, falls Ihr danach s', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12276', 'deDE', 'Ich heiße Euch im Namen aller auf der Mondgischt willkommen, $C. Ich hoffe, dass Ihr von unseren Vorräten profitiert, bevor Ihr wieder von Bord geht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12277', 'deDE', 'Seid wachsam an Bord der Elunes Segen, $R. Elune hat uns zwar mit der Entdeckung neuer Freunde gesegnet, aber wir hatten auch schon die ein oder andere Schwierigkeit auf See.$B$BDennoch, Seelen einzuh', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12278', 'deDE', 'Elune hat uns schon so oft mit den von uns gemachten Entdeckungen gesegnet. Dennoch müssen wir vorsichtig sein, $C.', 'Elune hat uns schon so oft mit den von uns gemachten Entdeckungen gesegnet. Dennoch müssen wir vorsichtig sein, $C.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12279', 'deDE', '', 'Verzeiht mir, $R, ich muss mich auf meine Arbeit konzentrieren.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12280', 'deDE', '', 'Es tut mir leid, ich habe nur wenig Zeit, um mit anderen als den Irrwischen und dem Kapitän zu sprechen. Ich muss von meinen Funden berichten. Verzeiht mir.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12281', 'deDE', 'Willkommen an Bord der Launischen Minna. Es sollte eine ruhige Fahrt bis zum Hafen sein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12282', 'deDE', 'Es ist erstaunlich, was man alles von einem Gnom lernen kann, wenn man ihn nur hart genug ausquetscht: Wie man sich geschickt aus Fesseln entwindet, köstliche Kekse bäckt und sogar, wie man Flugmaschi', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12283', 'deDE', 'So oft wie ich kann. Es ist eine fantastische Übung und sie hält mich auf dem Laufenden. So weiß ich immer, was sie vorhaben, und das ist - meiner Erfahrung nach - nie etwas Gutes.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12284', 'deDE', 'Urrrk... falls Euch dieser schmuddelige Smutje dort drüben sein Spezialgericht anbietet, dann tut Euch selbst einen Gefallen und werft es über Bord. Es könnte zwar ein paar Makruras töten, aber immer ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12285', 'deDE', '', 'Hallo, $R.$B$BLasst uns loslegen! Es ist nicht sinnvoll, auf Materialien zu warten, die vielleicht nie eintreffen werden.$B$BIch brauche meinen Amboss, um unsere Truppen richtig auszurüsten. Im Moment', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12286', 'deDE', '', 'Hallo $N.$B$BJetzt, da mein Amboss fertiggestellt ist, kann ich damit anfangen, unsere Truppen auszustatten. Ein wichtiger Schritt, aber es gibt noch viel zu tun.$B$BUnd was noch wichtiger für Euch is', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12287', 'deDE', '', 'Wir haben den Hafen erobert und das Schiff hier ist ein netter Bonus. Bedauerlich, dass es noch nicht seetüchtig ist.$B$BIch wurde angewiesen, in der Zwischenzeit die Zerschlagung der Naga an der Grün', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12288', 'deDE', 'Willkommen an Bord der Donnersturm!$B$BSchenkt den Leuten, die sich über Explosionen oder dergleichen beschweren, einfach keine Beachtung. Ich meine ja nur: Jeder macht mal Fehler. Die Leute sollten n', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12289', 'deDE', '<grummelt> Denke mal, die Berge waren dann einfach da... Fünfmal ist das passiert!', '', '<grummelt> Man könnte meinen, dass es ziemlich schwierig sein müsste, mit einem Zeppelin ins Meer zu stürzen. Man könnte auch meinen, dass es um einiges schwieriger sein sollte, das dann viermal zu sc', '', '<grummelt> Ich wünschte, ich könnte behaupten, dass wir es mittlerweile hinbekommen hätten, in keinen See mehr zu stürzen. Ich wünschte, ich könnte behaupten, dass wir das nur einmal geschafft hätten.', '', 'Was mich wundert, ist, dass die Äste der Kiefern unserem Schiff anscheinend nicht viel anhaben können.$B$BDas entschuldigt jedoch nicht unsere drei Abstürze in den Wald.', '', 'Hätte niemals gedacht, dass ich mal Teldrassil sehen würde... und noch weniger, dass wir hineinkrachen würden.', '', 'Wir konnten uns schon zweimal durch Donnerfels hindurchquetschen!', '', '<grummelt> Ich weiß noch immer nicht, wie wir uns ohne einen einzigen Kratzer durch Tausend Nadeln hindurchschlängeln konnten, nur um dann in der schimmernden Ebene eine Bruchlandung hinzulegen!', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12290', 'deDE', '', 'Wenn Ihr was Aufregendes erleben wollt, dann seid Ihr bei unserem Kapitän an der richtigen Adresse! Ich liebe diesen Job!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12291', 'deDE', 'Fragt lieber nicht...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12292', 'deDE', 'Ihr könnt mich nicht für unsere Missgeschicke verantwortlich machen... Ich kann ja nicht einmal sehen, in welche Richtung wir fliegen!$B$BWas vielleicht ein Grund sein mag, mich verantwortlich zu mach', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12294', 'deDE', 'Früher war ich unter dem Namen IoIoI bekannt. Doch dann gab es eine geheime Abstimmung in der Stadt und ich musste mich umbenennen. Ich hatte also die Wahl, entweder meinen Namen zu ändern oder den se', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12300', 'deDE', 'Ich bin froh, dass Ihr fragt. Unsere Anstrengungen, die Waffenkammer einzunehmen, sind bei $3233w Prozent unserer Hochrechnungen.$B$BIch weiß, dass Kampfmagierin Arynna und Herold Inuuro Eure Hilfe be', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12304', 'deDE', 'Wir versuchen Agamath, das erste Tor, zu durchbrechen. Ich bitte Euch, $C, reist zur Insel Quel\'Danas und helft so gut Ihr könnt!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12309', 'deDE', 'Sollte Kil\'jaeden durch den Sonnenbrunnen aufsteigen, wird diese Welt in einen Krieg gestürzt, wie er seit 10.000 Jahren nicht gesehen wurde!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12314', 'deDE', '', '\"Der Eiserne Adler\", hmpf. Es ist ein Passagierschiff, keine Kampfflugmaschine! Tja, aber was getan ist, ist getan - und ich lasse Kapitän \"Bombastik\" seinen Willen, solange der Prinz bezahlt.$B$BWie ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12315', 'deDE', 'Bin beschäftigt.', 'Bin beschäftigt.', 'Was? Ich hab hier was zu tun.', 'Was? Ich bin gerade beschäftigt.', 'Habe Dienst. GEHT WEG!', 'Habe Dienst. GEHT WEG!', 'Ihr wollt etwas repariert haben? Benutzt den Kauf-o-Mat 1-A.', 'Ihr wollt etwas repariert haben? Benutzt den Kauf-o-Mat 1-A.', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12317', 'deDE', 'Nicht jetzt, $GKumpel:Süße;... Ich bin beschäftigt. Bis später.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12318', 'deDE', 'Ich kommandiere das am straffsten organisierte Luftschiff in Azeroth. Es war einmal mit zwei leichten Kanonen bestückt und hatte genug Bomben geladen, um eine ganze Stadt dem Erdboden gleich zu machen', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12319', 'deDE', 'Wir haben unsere Vormachtstellung in den Sonnenweiten beinahe gefestigt. Wenn wir erreichen können, dass der Hafen Verstärkung bekommt, werden wir mit Sicherheit erfolgreich sein.$B$BWir haben unser Z', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12320', 'deDE', 'Wir haben es geschafft, $N. Mit den Sonnenweiten unter unserer Kontrolle ist der endgültige Sieg zum Greifen nahe.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12323', 'deDE', '', 'Ist das Denkmal nicht wunderschön? Es ist wichtig, dass wir uns an die Zeiten unserer Geschichte erinnern, in denen große Opfer erbracht wurden. Den Preis, den wir für alle Kriege zahlen, dürfen wir n', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12328', 'deDE', 'Eine entspannte Mannschaft ist eine tüchtige Mannschaft. Lasst sie sich erholen und sie werden mehr leisten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12329', 'deDE', 'Unser Kapitän ist spitze! Wenn wir anlegen, haben wir Zeit zum Entspannen. Darauf freue ich mich immer am meisten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12330', 'deDE', 'Erlaubt mir bitte ein paar Sekunden Rast.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12332', 'deDE', 'Ich bin gerade beschäftigt.', 'Ich bin gerade beschäftigt.', 'Ihr wollt etwas repariert haben? Benutzt den Kauf-o-Mat.', 'Ihr wollt etwas repariert haben? Benutzt den Kauf-o-Mat.', 'Ich habe gerade keine Zeit zum Reden.', 'Ich habe gerade keine Zeit zum Reden.', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12333', 'deDE', '', 'Der Flug von Grom\'Gol nach Unterstadt ist schön, aber etwas eintönig - was jedoch kein Grund ist, auf der Arbeit einzuschlafen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12334', 'deDE', 'Kommt herein - heraus aus Asche und Dreck. Trocknet Euch ein Weilchen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12335', 'deDE', 'Die Tauren Chieftains bleiben in Silbermond, während ich ihre nächste Tournee organisiere. Wenn sie nicht gerade proben, hängen sie auf dem Balkon über mir ab. Ihr könnt ruhig winken... sie stehen auf', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12336', 'deDE', 'Habt Ihr ihre Show in den Wäldern von Terokkar gesehen? Die war genial!', 'Habt Ihr ihre Show in den Wäldern von Terokkar gesehen? Die war genial!', 'Die Tauren Chieftains sind bestimmt die bekanntesten Musikanten in ganz Azeroth!', 'Die Tauren Chieftains sind wohl die bekanntesten Musikanten in ganz Azeroth!', 'Ich frage mich, wie lange sie wohl noch in Silbermond bleiben. Ich hoffe, dass sie hier spielen werden!', 'Ich frage mich, wie lange sie wohl noch in Silbermond bleiben werden. Ich hoffe, dass sie hier spielen werden!', 'Ich würde fast alles dafür geben, einmal dort oben mit ihnen reden zu können...', 'Ich würde fast alles dafür geben, einmal dort oben mit ihnen reden zu können...', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12338', 'deDE', '', 'Stellt euch dem Feind nicht unvorbereitet, $N. Ich kann Euch mit mächtigen Gegenständen ausstatten... vorausgesetzt, Ihr habt Euch uns würdig erwiesen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12339', 'deDE', 'Kaels Lakaien wird eine Lektion in Bescheidenheit erteilt werden. Wir werden die Dämmerklingenarmee zerschlagen und den Hafen der Sonnenweiten einnehmen.$B$BWir haben unser Ziel zu $3238w Prozent erre', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12340', 'deDE', 'Die Sonnenweiten sind unser! Kaels Hunde fliehen... es ist an der Zeit, sie von ihrem Leid zu befreien!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12341', 'deDE', 'Ich bin für die Roadies und Bühnenarbeiter der Tauren Chieftains verantwortlich.$B$BIhr wisst schon: das Fahren, Be- und Entladen der Wagen, Auf- und Abbauen der Bühne, Vorbereiten des Feuerwerks und ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12342', 'deDE', 'Schweitzer ist ein alter Freund von mir..., was nützlich ist - Freundschaft, mein\' ich. Bevor wir uns in Shattrath wieder trafen, dachte ich, dass ich das Gefühl vergessen hatte.$B$BHeutzutage ist er ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12343', 'deDE', 'Mmrrglglglg? Glbmr brmmrggl glmrrbmrgl.$B$BGrglgrlbrr mrgl!!!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12344', 'deDE', '<Ahlurglgr schaut verwundert, weil er Euch versteht.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12347', 'deDE', 'Angriff ist die beste Verteidigung - das wird Euch jeder sagen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12348', 'deDE', '', 'Eine gute Waffe nützt nicht viel, wenn man nicht lange genug lebt, um sie in der Hand zu halten, $C.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12349', 'deDE', 'Gawurgggl! Blrgl-brgl grgl!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12350', 'deDE', 'Ihr habt einen Trick gelernt, um mit uns zu reden, $R, doch ich bin nicht beeindruckt.$B$BWenn man das Wasser überquert, trifft man auf einige Verräter der Winterflossen, die getötet werden müssen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12352', 'deDE', 'Wisst Ihr, ich bin nicht blind. Ich sehe, was hier vor sich geht!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12354', 'deDE', 'Ahglrglglrglgrrr!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12357', 'deDE', 'Ich kam vor kurzem hierher, um die Winterflossenmurlocs zu beobachten. Als einer von ihnen verkleidet, hielt ich Abstand und notierte Fakten aus ihrem alltäglichen Leben.$B$BJedoch änderte sich das al', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12361', 'deDE', 'Meine Brüder und Schwestern sind verrückt geworden! Glaubt Ihr, Ihr könntet mir hier heraushelfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12362', 'deDE', 'Glug bmrgl grggl!', '', 'Mawurrgggl, gurgl lur!', '', 'Glumrggl?', '', 'Brggl-brggl Mrgl-Mrgl! Gluk!', '', 'Blrmrrgl gulrgl grr.', '', 'RAWGRLRLRLRRLGLRL!!!', '', 'Mrglrglrglrgl... glrgl?', '', 'Mrglmrgl Glrggl!', '');
INSERT INTO `npc_text_localized` VALUES ('12363', 'deDE', 'Keine Zeit, zu reden - Ich habe Arbeit zu erledigen!', '', 'Ihr könnt mich verstehen? Ich kann Euch verstehen!', '', '$R, warum sollte ich mit Euch sprechen wollen? Eure Art hat das Ganze doch überhaupt erst angerichtet!', '', 'Es müssen Muscheln gesammelt und Orcas getötet werden. Helft dabei oder geht aus dem Weg!', '', 'Blurglgr murbrgl... nur Spaß, ich kann Euch verstehen.', '', 'Habt Ihr schon rausbekommen, warum unsere Brüder und Schwestern drüben im Dorf verrückt geworden sind?', '', 'Ich wusste, wir hätten niemals in diese Höhlen gehen sollen. Da ist was nicht geheuer!', '', 'Mit dem neuen König Mrgl-Mrgl ist was faul, aber ich kann noch nicht genau sagen, was es ist.', '');
INSERT INTO `npc_text_localized` VALUES ('12364', 'deDE', 'Ich bin Atuik, Häuptling der Kalu\'ak von Kamagua.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12370', 'deDE', 'Verdammt seien Kael\'thas und seine teufelsblütigen Scheusale! Alles, worum wir uns bemüht haben ist dahin, aber was für eine Torheit zu glauben, wir würden nicht zurückschlagen. Lady Liadrin ist berei', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12371', 'deDE', '<Der Drachenfalke scheint von der Aufregung in der Umgebung beunruhigt zu sein.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12373', 'deDE', 'Das Schleifen Orgrimmars war nicht nur ein Angriff auf die Orcs, es war eine Verletzung der Ehre der Horde. Ein Angriff auf unsere Existenz!$B$BDer Lichkönig wollte die Saat der Furcht säen.$B$BDas hi', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12374', 'deDE', 'Ihr steht neben einem Freudenfeuer der Horde! Entweiht es und stampft es aus!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12376', 'deDE', 'An den Feuern des Sonnenwendfestes erfreut sich jedermann!', 'An den Feuern des Sonnenwendfestes erfreut sich jedermann!', 'Stark und wahrhaftig brennen unsere Feuer im ganzen Land!', 'Stark und wahrhaftig brennen unsere Feuer im ganzen Land!', 'Habt Ihr schon am Sonnenbaum getanzt?', 'Habt Ihr schon am Sonnenbaum getanzt?', 'Ich bewundere die Kunstfertigkeit von Feuerjongleuren.', 'Ich bewundere die Kunstfertigkeit von Feuerjongleuren.', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12377', 'deDE', 'Ihr steht neben einem Freudenfeuer der Allianz! Entweiht es und stampft es aus!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12381', 'deDE', 'Angst war schon immer unsere stärkste Waffe. Ganze Kriege wurden gewonnen oder verloren, indem man mehr Gewicht auf Angst und Zweifel legte. Wie also bekämpft man dann einen Feind, der keine Angst ken', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12382', 'deDE', 'Wir müssen diesen Außenposten auf den Marsch ins Sholazarbecken vorbereiten.$B$BSteht dort nicht nur so rum, $C. Geht und repräsentiert die Horde mit Ehre und vergossenem Blut!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12384', 'deDE', '', 'Ich muss diesen Schlamassel beseitigen, aber das werde ich klaglos tun.$B$BUnd wenn Ihr auch nur einen Funken Hordenstolz besitzt, dann helft Ihr mir!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12387', 'deDE', '', 'Überall um Euch herum sprechen die Geister. Hört Ihr sie, $R?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12388', 'deDE', 'Hier liegt der Leichnam einer Wache von Stahlkiefers Karawane.', 'Hier liegt der Leichnam einer Wache von Stahlkiefers Karawane.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12389', 'deDE', 'Hier liegt der Leichnam eines Arbeiters von Stahlkiefers Karawane.', 'Hier liegt der Leichnam eines Arbeiters von Stahlkiefers Karawane.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12390', 'deDE', 'Alles, für dessen Erhalt wir kämpfen, sucht der Schattenhammerkult zunichte zu machen. Wir haben einen Eid geschworen, um diese Katastrophe zu verhindern, $R.', 'Alles, für dessen Erhalt wir kämpfen, sucht der Schattenhammerkult zunichte zu machen. Wir haben einen Eid geschworen, um diese Katastrophe zu verhindern, $R.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12394', 'deDE', 'Ich bin entehrt. Ich hätte mit dem Weissager in Magmut sterben sollen.$B$BÜberlasst mich meinen Gedanken.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12395', 'deDE', 'Das Element der Luft hier ist ruhelos. Seid auf der Hut... Sie werden Euch nicht unbeschadet passieren lassen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12403', 'deDE', 'Es tut mir weh, zu sehen, wie meine Ahnen in ihren Gräbern gestört werden.$B$BWir müssen den verfluchten Geist von Truppenkommandant Stahlkiefer erschlagen, demjenigen, der törichterweise seine Karawa', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12413', 'deDE', 'Nur Ihr könnt mich vor Kaganishu retten, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12417', 'deDE', 'Wir haben nicht wenige Windreiter auf Kundschaftermissionen gen Westen verloren. Die Geißel geht rücksichtslos gegen alle möglichen Spione vor.$B$BZum Glück habe ich eine Lösung gefunden, bei der nur ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12419', 'deDE', 'Ich wurde entehrt. Ich hätte mit dem Weissager in Magmut sterben sollen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12422', 'deDE', 'Garroshs Landeplatz und Pal\'ea wurden beide vom Nebel ausgelöscht. Wir sind die einzigen, die verrückt genug sind, hier zu bleiben, Kumpel!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12423', 'deDE', '', 'Wie soll ich irgendetwas bewerkstelligen, wenn Kurbelzisch ständig hinter mir steht und ich nicht mal meine Sachen habe?!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12424', 'deDE', 'Was Ihr hier an dieser Küste seht, ist nicht das Produkt der Geißel. Tatsächlich glauben wir, dass auch die Geißel Angst vor dem Nebel hat - das könnte auch der Grund sein, warum sie nicht hierher kom', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12426', 'deDE', 'Seitdem ich hier angekommen bin, habe ich Schrecken gesehen, die ich vorher nur aus meinen schlimmsten Alpträumen kannte, doch nichts hätte mich auf das vorbereiten können, was hinter dem Nebel wartet', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12427', 'deDE', '', 'Ich... Seid Ihr...$B$B<Muhtoo fröstelt.>$B$BSeid Ihr hier, um mich zu retten?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12433', 'deDE', 'Ihr seid ein Anblick für entzündete Augen! Ich bin zu schwach, um die Glieder dieser verrosteten Kette an der Kugel zu zerstören.$B$BSteht da nicht einfach so rum! Macht mich los!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12435', 'deDE', 'Ich habe hier im Moment alle Hände voll zu tun, $C.$B$BWas kann ich für Euch tun?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12440', 'deDE', '', 'Sprecht langsam, Kind, ich bin Eure seltsame Sprache nicht gewohnt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12441', 'deDE', '', 'Bitte, sprecht leise, $R. Hamat ist alt und braucht ihre Ruhe.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12456', 'deDE', 'Also gut, setzt Euch; das könnte eine Weile dauern.$B$BAls wir ursprünglich in diese gottverlassene, gefrorene Hölle kamen, gab General Arlos mir und meinen fliegenden Teufelskerlen unten in der Valia', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12457', 'deDE', 'Jedenfalls mussten wir massig Sand und Öl für die Maschinerie pumpen. Die nahegelegenen Teiche erwiesen sich dafür als perfekt.$B$BDer Sand zeigte teilweise sogar magische Eigenschaften... wie hoch is', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12458', 'deDE', 'Allerdings hab\' ich das erst später erfahren, weil ich zu der Zeit nicht da draußen war.$B$BAls die dann die Saugkraft der Pumpe hochgedrieselt haben, kamen Stücke eines Roboters hoch, der wie ein Gno', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12459', 'deDE', 'Nach ein paar Tagen Funkstille habe ich ein Erkundungsteam zur Plattform geschickt. Sie sind niemals zurückgekehrt.$B$BAm nächsten Tag schickte ich eine weitere Gruppe mit dem gleichen Ergebnis und ve', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12460', 'deDE', 'Wie ich sagte, was wir da draußen fanden, spottete jeder Beschreibung.$B$BMeine Leute waren nirgendwo zu sehen, doch an ihrer statt gab es dort eine wahrhafte Armee von Robotern und Androiden, die zur', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12461', 'deDE', 'Er nennt sich selbst Ritzelmeister Mechazod. Als wir ankamen, war er damit beschäftigt, die Überlebenden in mechanische Geschöpfe zu verwandeln!$B$BEr begrüßte uns herzlich und erklärte uns, er sei ei', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12462', 'deDE', 'Die Mechaniker hier hatten ihn wieder zusammengebaut und ihm neues \"Leben\" geschenkt.$B$BUnd jetzt, um seine Dankbarkeit zu zeigen, würde er uns alle von dem, wie er es nannte, \"Fluch des Fleisches\" h', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12463', 'deDE', 'Es sah mir nicht wirklich nach einer \"Heilung\" aus und ich wollte da nicht rumstehen und irgendeinem verrückten Roboter zuhören, während er meine Leute abschlachtete!$B$BWir taten, was jeder geistig g', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12464', 'deDE', 'Und so sieht es jetzt für uns aus, $N.$B$BWir können wohl kaum mit all dem allein fertig werden; und mal ehrlich, ich fühle mich hier etwas überfordert. Jinky weiß, was getan werden muss. Ihr solltet ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12472', 'deDE', 'Die Handlungen dieser Deserteure beschämen uns alle. Eine Schande so mächtig, dass sie mit eigenen Augen bezeugt werden muss, damit man sie versteht. Deshalb ist Ug\'thor hier... Der Junge muss lernen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12475', 'deDE', '', 'Seid in dieser Gegend vorsichtig, $R. Die Einheimischen sind skrupellos.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12477', 'deDE', 'Ich bin vom Tuskarrgesetz her verpflichtet, meine Angehörigen zu rächen... Diese Mörder haben Pal\'ea bis auf die Grundmauern niedergebrannt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12478', 'deDE', 'Ich habe Bauern, Edelleute, Händlerssöhne und Bettler behandelt. Erlaubt mir, eine Weisheit mit Euch zu teilen - wir sind alle gleich, wenn wir dem Tod ins Angesicht schauen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12485', 'deDE', 'Ist Euer Anliegen gerecht und Euer Herz rein? Falls ja, seid Ihr hier willkommen - bei den Druiden für den ethischen und humanen Umgang mit Tierarten. Solange Ihr nicht den dunklen Pfad der Verdorbenh', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12489', 'deDE', 'Dieser Aufklärungspilot hat den Absturz seiner Staffel nicht überlebt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12494', 'deDE', 'So, $N, an welchem Gegenstand seid Ihr interessiert?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12495', 'deDE', 'Ah... Kuriositäten und Seltenheiten. Wir spezialisieren uns auf Azeroths einzigartigere Gegenstände für den anspruchsvollen Abenteurer.$B$BNun, da Ihr mit mir sprecht, müsst Ihr ja schon eine Vorstell', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12497', 'deDE', '', 'Wir haben es geschafft, $N. Mar\'nah weiß jetzt, wie man außergewöhnliche Edelsteine transmutiert, und ich weiß, wie man sie bearbeitet.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12498', 'deDE', 'Fürchtet Euch nicht, $R. Wir verfolgen dasselbe Ziel.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12500', 'deDE', 'Ich bin Kalecgos vom blauen Drachenschwarm.$B$BSeit einiger Zeit wache ich nun schon über die sich regenden Kräfte des Sonnenbrunnens, doch jetzt erkenne ich, dass seine schreckliche Kraft das Ende de', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12502', 'deDE', 'Kael\'thas zu besiegen, ist eine Aufgabe, die Eure ganze Aufmerksamkeit verdient.$B$BUnd wärt Ihr dazu imstande, eine Armee aufzustellen und Euch in den Sonnenbrunnen zu begeben, dann fändet Ihr mehr a', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12504', 'deDE', 'Seid gegrüßt, $C. Seid Ihr gekommen, um dieser Flamme Eure Ehrerbietung zu erweisen?$B$BVergesst nicht: Unsere Flammen des Sonnenwendfests brennen in jedem Land, in dem sich eine Siedlung der Allianz ', 'Seid gegrüßt, $C. Seid Ihr gekommen, um dieser Flamme Eure Ehrerbietung zu erweisen?$B$BVergesst nicht: Unsere Flammen des Sonnenwendfests brennen in jedem Land, in dem sich eine Siedlung der Allianz ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12506', 'deDE', 'Seht die Flammen des Sommers!', 'Seht die Flammen des Sommers!', 'Vorsicht, verbrennt Euch nicht!', 'Vorsicht, verbrennt Euch nicht!', 'Habt Ihr ein schönes Sonnenwendfest?', 'Habt Ihr ein schönes Sonnenwendfest?', 'Willkommen! Habt Ihr schon der hiesigen Flamme Eure Ehrerbietung erwiesen?', 'Willkommen! Habt Ihr schon der hiesigen Flamme Eure Ehrerbietung erwiesen?', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12507', 'deDE', '', 'Diese Welt hat auf eine Art und Weise Schaden genommen, die sich unseren Augen entzieht. Selbst der Fluss der Zeit fühlt sich an, als wäre er irgendwie anders.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12510', 'deDE', 'Die Arenakampfmeister befinden sich entlang der Mauer im Südosten des unteren Viertels.', 'Die Arenakampfmeister befinden sich entlang der Mauer im Südosten des unteren Viertels.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12511', 'deDE', 'Während Nesingwarys Vorgehen gegen die Wildtiere von Nordend zum erliegen gekommen sind, ist es nur eine Frage der Zeit, bevor er einen erneuten Übergriff versucht. Ihr müsst wachsam bleiben, $N!$B$BU', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12521', 'deDE', '', 'Ich will verdammt sein, wenn uns diese verteufelte Geißel vertreiben kann! Wir Siedler haben alles aufgegeben, um in Nordend ein neues Leben anzufangen.$B$BIhr könnt sicher sein, dass wir uns mit Händ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12524', 'deDE', 'Der Brocken strahlt eine bittere Kälte aus, welche die Luft um ihn herum und die Plattform, auf der er steht, abkühlt. In seinem Inneren pulsiert ein blaues Licht - es scheint fast so, als ob es auf e', 'Der Brocken strahlt eine bittere Kälte aus, welche die Luft um ihn herum und die Plattform, auf die er steht, abkühlt. In seinem Inneren pulsiert ein blaues Licht - es scheint fast so, als ob es auf e', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12525', 'deDE', 'Die Geißel ist überall! Was für ein... Alptraum!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12526', 'deDE', 'Arthas glaubt, ein paar seiner untoten Käfer würden uns zurück in die östlichen Königreiche jagen... Dem werden wir\'s schon zeigen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12527', 'deDE', 'Die dunklen Mächte treiben ihr Unwesen in diesem Land, $N. Haltet Eure Augen offen und Euren Mut beisammen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12528', 'deDE', 'Einige von uns kommen um den Militärdienst herum. Ein guter Fischer ist immer zu gebrauchen... selbst Generäle müssen essen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12529', 'deDE', 'Wieder ein Tag in der Küche. Das wird auch mit der Zeit langweilig.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12530', 'deDE', 'Meine Schicht ist schon fast vorbei. Kann\'s kaum erwarten, mich schlafen zu legen - wenn Ihr wisst, was ich meine.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12534', 'deDE', '', 'Arthas war einst ein menschlicher Prinz. Die Armee, die er nach Nordend führte, erlitt ein schreckliches Schicksal.$B$BMein Bruder Thassarian gehörte auch dieser Armee an.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12538', 'deDE', 'Wir sind Bergleute, Bauern und Handwerker - keine Soldaten. Jedoch werden wir unser Land mit bloßer Faust verteidigen, wenn es sein muss.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12553', 'deDE', 'Kann ich Euch etwas anbieten? Meine Auswahl ist etwas begrenzt, aber das ist ja unter den gegebenen Umständen zu erwarten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12554', 'deDE', 'Hä?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12555', 'deDE', 'Für sein Land zu sterben ist die Pflicht eines Soldaten. Doch wenn es Hochverrat ist, diesen Krieg Aberwitz zu nennen, so bin ich ein Verräter.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12556', 'deDE', '', 'Ich bin gerade sehr beschäftigt, $R. Lasst mich bitte allein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12566', 'deDE', 'Konzentriert Euren Geist, Sterblicher. Das ist die Front eines Krieges, wie Ihr ihn noch nie zuvor gesehen habt.$B$BIhr müsst Eure Fähigkeiten vollends beherrschen, um von Nutzen zu sein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12572', 'deDE', 'Werden wir bereit sein, wenn Ahune erscheint?', 'Werden wir bereit sein, wenn Ahune erscheint?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12575', 'deDE', 'Das Land ist in Aufruhr!$B$BDer Dunkle reitet wieder weiter mit seinen San\'layn, die Drachen kämpfen in ihrem großen Nexuskrieg und die Vrykul sind wiedererwacht.$B$BEure Leute segeln auf ihren Holzsc', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12576', 'deDE', '', 'Es ist mir eine große Freude, Euch wiederzusehen, $N.', '', '$N! Ich habe mich schon gefragt, wo Ihr gewesen seid. Was hat Euch aufgehalten?', '', 'Ich vertraue darauf, dass Ihr alle Vorsichtsmaßnahmen trefft...$B$BDies ist ein gefährlicher Ort, und ich könnte es mir nicht verzeihen, wenn Euch etwas zustoßen sollte.', '', 'Wir müssen uns beeilen, $N. Malygos\' zerstörerische Pläne werden bald unumkehrbar sein.', '', 'In solchen Zeiten sind gute Freunde ein unschätzbares Gut.$B$BPasst auf Euch auf, $N. So gut das an einem Ort wie diesem geht...', '', 'Hättet Ihr mich nicht aus diesem Kerker befreit, dann hätte ich inzwischen vermutlich aufgegeben.$B$BIch habe es nicht vergessen...', '', 'Wenn unsere Pflicht hier getan ist, werde ich einen Weg finden, Euch für meine Rettung zu belohnen.', '', 'Meine Stärke kehrt langsam zurück...$B$BIch hoffe, ich kann Euch bald die Schönheit meiner wahren Form zeigen.');
INSERT INTO `npc_text_localized` VALUES ('12577', 'deDE', '', 'Oh...$B$BWo bin ich?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12578', 'deDE', 'Die Kvaldir kennen keine Gnade, $N. Doch ich kann das heilige Kaskala nicht verlassen. Wenn wir die Geister nicht verlassen, verlassen uns die Geister auch nicht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12579', 'deDE', 'Kaskala wird angegriffen! Wir müssen sie zurückschlagen, bis wir fliehen können.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12580', 'deDE', 'Verfluchte Riesenmenschen! Wenn sie einen Kampf wollen, sollen sie einen haben!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12582', 'deDE', 'Seid gegrüßt, $C. Seid Ihr gekommen, um dieser Flamme Eure Ehrerbietung zu erweisen?$B$BVergesst nicht: Unsere Flammen des Sonnenwendfests brennen in jedem Land, in dem sich eine Siedlung der Horde be', 'Seid gegrüßt, $C. Seid Ihr gekommen, um dieser Flamme Eure Ehrerbietung zu erweisen?$B$BVergesst nicht: Unsere Flammen des Sonnenwendfests brennen in jedem Land, in dem sich eine Siedlung der Horde be', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12585', 'deDE', 'Dieser junge Tuskarr scheint eher im Kampf erschlagen worden als ertrunken zu sein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12587', 'deDE', 'Dann bin ich froh, dass Ihr gekommen seid. Nun, da Ihr über unsere Feuer und die Feuer unserer Feinde Bescheid wisst... und darüber in Kenntnis gesetzt wurdet, die unsrigen zu ehren und die der andere', 'Dann bin ich froh, dass Ihr gekommen seid. Nun, da Ihr über unsere Feuer und die Feuer unserer Feinde Bescheid wisst... und darüber in Kenntnis gesetzt wurdet, die unsrigen zu ehren und die der andere', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12590', 'deDE', 'In einem Wort, Magie.$B$BVerantwortungslose Sterbliche, bei ihrer kurzen Lebensspanne denken sie nicht an die Folgen ihrer Taten.$B$BArkane Energien wurden zu großflächig abgezapft, wodurch das Gleich', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12591', 'deDE', 'Wir machen Fortschritte, $R, doch viel zu langsam.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12592', 'deDE', 'Wir dürfen nicht versagen! Das Schicksal Azeroths steht auf dem Spiel...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12593', 'deDE', 'Malygos... seine Macht durchwirkt Sphären. Der Herr über alle Magie, Meister des blauen Drachenschwarms! Er ist der blaue Drachenaspekt, eine der ältesten Kreaturen Azeroths.$B$BMan sagt, er erhole si', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12596', 'deDE', '', 'Ich bin froh, dass Ihr hier seid, $C. Aus militärischer Sicht betrachtet, ist die Situation hier auf dem Sonnenbrunnenplateau alles andere als rosig.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12597', 'deDE', 'Hauptmann Selana hat mich damit beauftragt, Euch bei Euren Vorhaben hier auf dem Sonnenbrunnenplateau zu unterstützen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12598', 'deDE', '', 'Die Offensive der Zerschmetterten Sonne hat große Fortschritte in den äußeren Gebieten von Quel\'Danas gemacht. Kil\'jaedens Streitkräfte werden zurückgedrängt und in dem ganzen Chaos konnte ich mit ein', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12599', 'deDE', '', 'Kil\'jaedens Verteidigungstruppen hier auf dem Sonnenbrunnenplateau sind stärker als alles, was uns bisher draußen auf der Insel begegnet ist. Die Legion hat sogar unsere Arkanwächter mit Hilfe von Teu', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12600', 'deDE', '', 'Ich weiß es nicht, aber ich bin froh, dass sie das getan haben. Die Pause verschaffte Anachoret Elbadon genug Zeit, um viele Männer und Frauen, die unter meinem Kommando standen, zu retten. Ich habe e', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12601', 'deDE', '', 'Nutzt die Tatsache aus, dass ihre Beschützer der Sonnenklingen deaktiviert sind. Wenn ein Späher der Sonnenklingen Euch entdeckt, dann bin ich mir sicher, dass er versuchen wird einen in der Nähe befi', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12604', 'deDE', 'Alle Barrieren, die zwischen Euch und dem Sonnenbrunnen standen, wurden zerstört. Begebt Euch zum Sonnenbrunnen und verhindert, dass Kil\'jaeden diese Welt betritt!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12606', 'deDE', 'Gemeinsam mit einem Freund bin ich hierher gekommen, um eine junge Menschenfrau aus den Klauen des Legionsfürsten Kil\'jaeden und seiner Diener zu befreien.$B$BWie Ihr schon wisst, versucht Kael\'thas d', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12607', 'deDE', 'Madrigosa hat mich auf dieser Reise begleitet, doch wir wurden im Sonnenbrunnen voneinander getrennt.$B$BMöge das Schicksal ihr wohl gesinnt sein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12608', 'deDE', 'Anveena ist keine bloße Sterbliche, $R. Diejenigen, die ihre wahre Natur erkannt haben, schrecken vor nichts zurück, um sie in ihren Besitz zu bekommen.$B$BEinst wurde ich von ihr gerettet und nun möc', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12609', 'deDE', 'Genießt Ihr das Fest, $C?$B$BWährend die Flammenbewahrer sich den Feuern der Gegenwart widmen, bin ich eher ein Historiker, der sich der Vergangenheit dieses Festes verschrieben hat. Natürlich dokumen', 'Genießt Ihr das Fest, $C?$B$BWährend die Flammenbewahrer sich den Feuern der Gegenwart widmen, bin ich eher ein Historiker, der sich der Vergangenheit dieses Festes verschrieben hat. Natürlich dokumen', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12610', 'deDE', 'Willkommen in unserem Lager, $C. Ihr dürft Euch hier gerne ausruhen, wenn Ihr wollt.', 'Willkommen in unserem Lager, $C. Ihr dürft Euch hier gerne ausruhen, wenn Ihr wollt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12611', 'deDE', 'Hier gibt es nichts mehr für uns.', 'Hier gibt es nichts mehr für uns.', 'Meine Familie wurde ohne Gnade abgeschlachtet. Sogar die Kinder.', 'Meine Familie wurde ohne Gnade abgeschlachtet. Sogar die Kinder.', 'Möge der Lichkönig in den Feuern der Hölle schmoren für das, was er über dieses Land gebracht hat.', 'Möge der Lichkönig in den Feuern der Hölle schmoren für das, was er über dieses Land gebracht hat.', 'Wir sind jetzt ein Volk, das seiner Heimat beraubt wurde.', 'Wir sind jetzt ein Volk, das seiner Heimat beraubt wurde.', 'Die Geißel ist eine furchtlose Maschinerie, die darauf aus ist, alles Leben in dieser Welt zu vernichten.', 'Die Geißel ist eine furchtlose Maschinerie, die darauf aus ist, alles Leben in dieser Welt zu vernichten.', 'Der, den man Höllschrei nennt, könnte vielleicht unsere einzige Hoffnung sein.', 'Der, den man Höllschrei nennt, könnte vielleicht unsere einzige Hoffnung sein.', 'Nur die Horde kann uns jetzt noch retten.', 'Nur die Horde kann uns jetzt noch retten.', '', '');
INSERT INTO `npc_text_localized` VALUES ('12617', 'deDE', '<Budd hält sehr still und tut so, als würde er Euch nicht hören. Als Ihr näher hinseht, bemerkt Ihr, wie etwas Sabber unter seiner Maske hervorläuft.>', '', '<Budd wackelt mit seinen Hüften vor und zurück und murmelt etwas, das nach Trollsprache klingt.>', '', 'Geh weg, Mann.', '', 'Nee, Mann... keine Zeit zum Reden, Mann. Ich brauch\' Schlaf...', '', 'Trollpower, Mann!', '', 'Was stimmt\'n nicht mit dir, Mann? Haste noch nie \'nen Troll gesehen?', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12618', 'deDE', 'Wird die Horde mir die Chance bieten, die Geißel zu bekämpfen?', 'Wird die Horde mir die Chance bieten, die Geißel zu bekämpfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12619', 'deDE', 'Um mein Volk zu rächen, um die Pest, die unser Land heimgesucht hat, zu vertreiben - werde ich den Eid schwören. Ich verpflichte alles, was ich habe, und alles, was ich bin, der Horde.', 'Um mein Volk zu rächen, um die Pest, die unser Land heimgesucht hat, zu vertreiben - werde ich den Eid schwören. Ich verpflichte alles, was ich habe, und alles, was ich bin, der Horde.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12620', 'deDE', 'Lok\'tar ogar! Sieg oder Tod - diese Worte binden mich an die Horde, denn sie sind die heiligste und die fundamentalste aller Wahrheiten für einen Krieger der Horde.$B$BIch verpflichte mein Fleisch und', 'Lok\'tar ogar! Sieg oder Tod - diese Worte binden mich an die Horde, denn sie sind die heiligste und die fundamentalste aller Wahrheiten für einen Krieger der Horde.$B$BIch verpflichte mein Fleisch und', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12623', 'deDE', 'Alle drei Tore auf dem Sonnenbrunnenplateau wurden durchbrochen, $N.$B$BIhr müsst Eure Streitkräfte mobilisieren und verhindern, dass Kil\'jaeden Azeroth betritt, bevor es zu spät ist!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12626', 'deDE', '', 'Peitschennarbe wird nie wieder so schön sein wie früher, aber dieser Schiff fahrende Abschaum wird seine Taten bereuen, das schwöre ich!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12627', 'deDE', '', 'Es ist unsere Pflicht, diese Taunka zu beschützen und ihnen jedes bisschen Ruhe und Frieden zu schenken, das wir geben können.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12634', 'deDE', 'Was glotzt\'n so, Kumpel?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12636', 'deDE', 'Ich habe nie verstanden, warum manche Piraten ihre Schätze vergraben. Ich... ich habe meine Schätze lieber in greifbarer Nähe.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12637', 'deDE', 'Die große Meeresschildkröte \"Wellenreiter\" reist von dieser Anlegestelle zum Hafen Moa\'ki in der Drachenöde.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12638', 'deDE', 'Nordspeer, der dampfgetriebene Eisbrecher der Allianz, schippert von hier zum Hafen von Menethil im Sumpfland über das Meer in den östlichen Königreichen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12640', 'deDE', 'Der Hafen Moa\'ki ist mit zwei großen Meeresschildkröten gesegnet: Wellenreiter und Grüne Insel. Sie schwimmen zwischen Unu\'pe und Kamagua hin und her.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12641', 'deDE', '', 'Begierig darauf, nach Nordend zu kommen, hm? Die dampfgetriebene Kraken segelt von hier aus zur Valianzfeste in der boreanischen Tundra.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12642', 'deDE', 'Falls Ihr nach Nordend reisen wollt, der Eisbrecher Nordspeer segelt von hier aus nach Valgarde im heulenden Fjord.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12644', 'deDE', 'He dort! Ihr vermisst die östlichen Königreiche, was? Nun, die Kraken segelt zurück nach Sturmwind, falls Ihr wirklich zurückkehren wollt. Also, ich weiß, mir würde das wärmere Klima sehr gefallen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12645', 'deDE', 'Unsere gesegnete, große Meeresschildkröte, Grüne Insel, reist von hier zum Hafen Moa\'ki in der Drachenöde.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12647', 'deDE', 'He dort! Wenn Ihr nach Nordend wollt, dann sucht Ihr nach dem Schiff \'Windesmacht\'. Sie fliegt Euch direkt zur Kriegshymnenfeste in der boreanischen Tundra.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12649', 'deDE', '', 'Nordend wartet, $R. Die majestätische Wolkenkuss bringt Euch über das Meer direkt zum Hafen der Vergeltung im heulenden Fjord.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12650', 'deDE', 'Auf dem Weg nach Hause, $C? Die Windesmacht bringt Euch dorthin, vorausgesetzt Euer Zuhause liegt in Kalimdor. Sie fliegt von hier direkt nach Durotar.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12657', 'deDE', 'Gesucht wegen Hochverrats und anderer verräterischer Tätigkeiten gegen die Horde!$B$BAlle Kopfgelder werden von Hauptmann Gort ausgezahlt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12658', 'deDE', 'Hallöchen! Kurbelzischs Piloten sind die Besten der Besten! Und das bleiben wir auch durch beständiges Training. Dazu gehört auch das Training mit dem Fallschirm. Ihr wisst nie, wann Ihr Euren Schirm ', 'Hallöchen! Kurbelzischs Piloten sind die Besten der Besten! Und das bleiben wir auch durch beständiges Training. Dazu gehört auch das Training mit dem Fallschirm. Ihr wisst nie, wann Ihr Euren Schirm ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12659', 'deDE', 'N\' guten, mein Freund! S-setzt Eusch un\' trinkt einen mit, hm?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12663', 'deDE', 'Alles, was ich wollte, war, meinem König erneut dienen zu dürfen. Für die Menschheit, gegen einen gemeinsamen Feind.$B$BStattdessen muss ich feststellen, dass unsere Reihen von Kultistenabschaum infil', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12664', 'deDE', 'Von hier aus erhalten die Kultisten ihre Anweisungen. Ihr Anführer sollte jeden Moment hier auftauchen.$B$BDiese Gelegenheit dürfen wir uns nicht entgehen lassen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12666', 'deDE', 'Hä? Was?! Geht weg.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12667', 'deDE', '', 'Wenn ich eine neue Kriegsmaschine bauen kann, dann werde ich am Zorntor auch gutes Geld dafür bekommen. $B$BEinige unserer vorherigen Versionen waren nicht völlig... stabil, aber meine Flugmaschine so', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12668', 'deDE', 'Es gibt Geld zu verdienen! Wie Vati immer sagte - es gibt keine bessere Zeit, abzusahnen, als in Zeiten des Krieges.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12669', 'deDE', 'Ihr fühlt eine seltsame Präsenz, als Ihr vor dem uralten Götzen steht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12673', 'deDE', 'Willkommen! Falls Ihr einen Ort sucht, an dem Ihr Euch ausruhen könnt, so habe ich eine Lösung für dieses Problem.', 'Willkommen! Falls Ihr einen Ort sucht, an dem Ihr Euch ausruhen könnt, so habe ich eine Lösung für dieses Problem.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12678', 'deDE', 'Wir werden Arthas zermalmen! Die Geißel ist nichts im Angesicht der Macht der Horde!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12687', 'deDE', 'Willkommen, $C! Ich hoffe, Ihr leidet nicht unter Höhenangst!$B$BIch habe hier einen Übungsfallschirm für Euch. Diese Fallschirme sind für schnelle Landungen. Falls Ihr sanft zu Boden gleiten wollt, b', 'Willkommen, $C! Ich hoffe, Ihr leidet nicht unter Höhenangst!$B$BIch habe hier einen Übungsfallschirm für Euch. Diese Fallschirme sind für schnelle Landungen. Falls Ihr sanft zu Boden gleiten wollt, b', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12694', 'deDE', 'Ihr solltet lieber von hier verschwinden, wenn Ihr nicht in einem von diesen Käfigen landen wollt...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12696', 'deDE', 'Ich danke Euch, $C, dass Ihr dafür gesorgt habt, dass ich den Brief meines Bruders bekomme.$B$BSeid beruhigt, sein Tod wird nicht vergebens sein. Der blaue Drachenschwarm wird dafür zahlen.$B$BIch wer', 'Ich danke Euch, $C, dass Ihr dafür gesorgt habt, dass ich den Brief meines Bruders bekomme.$B$BSeid beruhigt, sein Tod wird nicht vergebens sein. Der blaue Drachenschwarm wird dafür zahlen.$B$BIch wer', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12698', 'deDE', 'Tut das Eure, um die Geißel fernzuhalten, Soldat! Erstattet Bericht über alle verdächtigen Aktivitäten, tötet erst und stellt dann Fragen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12699', 'deDE', 'Merkt Euch meine Worte, diese verlassenen Narren sind noch unser aller Tod!', '', 'Wenn Ihr mich fragt, und das TATET Ihr, so denke ich, dass wir alle Apotheker töten sollten, bevor es zu spät ist!', '', 'Die Horde steht für Ehre und Krieg. Ich bin mir sicher, dass an dem, was die Apotheker tun, nichts Ehrenwertes ist.', '', 'Ich habe diese verlassenen Idioten da unten im Auge. Bei denen kann man nie zu vorsichtig sein!', '', 'Solltet Ihr mit dem Doktor dort drüben zu tun haben, so seid vorsichtig. Was auch immer sie im Schilde führen, es ist unnatürlich.', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12701', 'deDE', 'Mann, die Wüsten westlich dieses Ortes sin\' voll mit Gesocks. Haltet Euch davon fern, wenn Euch Euer Leben lieb ist!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12703', 'deDE', 'Mein Auftrag in Agmars Hammer reicht weit darüber hinaus, mich um die Tiere in den Ställen zu kümmern.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12705', 'deDE', 'Ende? Ich? Hehe, wohl kaum, $GJunge:Mädel;. Das war nur ein Kratzer.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12706', 'deDE', 'Beeindruckend, oder? Diese Riesen wären perfekt geeignet als Kanonenf... äh, als Verbündete. Dieser hier ist ihr Anführer, aber er weigert sich weiterhin, mit mir zu sprechen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12707', 'deDE', 'Hallo, $R. Seid willkommen, diese Schlucht als Zuflucht vor den Drachen mit uns zu teilen. Hütet Euch jedoch vor dem Goblinwicht... denen kann man nicht trauen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12708', 'deDE', 'Mit genug Geld könnte ich die Weltherrschaft an mich mit reißen....', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12709', 'deDE', 'Die Steuerung steht vor einem Haufen dampfender Trümmer. Unter Euch hört Ihr das Arbeiten eines Motors.', 'Die Steuerung steht vor einem Haufen dampfender Trümmer. Unter Euch hört Ihr das Arbeiten eines Motors.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12713', 'deDE', 'Willkommen im Wyrmruhtempel, $GReisender:Reisende;.$B$BHier konferieren die Drachenschwärme in Frieden miteinander inmitten der Knochen ihrer ehrwürdigen Vorfahren.$B$BZumindest war es friedlich, bevo', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12714', 'deDE', '', 'Möchtet Ihr wieder nach unten, $C?$B$BIch kann dafür sorgen, dass Euch einer dieser Drachen sicher an Euer Ziel bringt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12715', 'deDE', 'Lasst mich in Ruhe, Narr!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12718', 'deDE', 'Was? Ihr wollt, dass ich Euch etwas beibringe, $C? Macht Euch besser bereit, zu arbeiten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12720', 'deDE', 'Ihr könnt in den Baracken schlafen, wenn Ihr möchtet. Weckt mir nur nicht meine Männer auf oder ich befördere Euch eigenhändig hinaus in den Schnee.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12721', 'deDE', 'Habt Dank dafür, dass Ihr mich gerettet habt, $N. Euren Mut und Eure Entschlossenheit werde ich niemals vergessen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12722', 'deDE', 'Es wäre eine Ehre, im Gefecht neben Euch als Bruder der Horde zu kämpfen, $N. Ich nehme Euer Angebot an.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12725', 'deDE', 'Ihr seid kein richtiger Mann bis Ihr einen Drachen getötet habt... Mann.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12726', 'deDE', 'Nichts kickt mehr als die Drachenjagd.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12727', 'deDE', '', 'Hallo, $R. Ich bedaure, dass Ihr unser heiliges Land zum ersten Mal in einem solchen Zustand sehen müsst.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12730', 'deDE', 'Warum stört Ihr mich?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12732', 'deDE', 'Heh dort. Sagt\'s, wenn Ihr was braucht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12733', 'deDE', 'Monstrositäten der Geißel... Die werden noch früh genug sehen, was sie erwartet.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12734', 'deDE', 'Ich habe Essen, solltet Ihr hungrig sein, und Ihr könnt mein Zelt benutzen, falls Ihr rasten wollt.', 'Ich habe Essen, solltet Ihr hungrig sein, und Ihr könnt mein Zelt benutzen, falls Ihr rasten wollt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12735', 'deDE', 'Es schneit vielleicht nicht da draußen, aber bei den Nächten hier kann Euch leicht die Nase abfrieren. Die Kojen sind im hinteren Teil, lasst Euch nur nicht zur Plage werden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12736', 'deDE', 'He, nichts gegen Euch, $GJüngelchen:Mädel;, aber Ihr solltet vielleicht einen anderen Schlafplatz finden. Ihr wollt doch nicht, dass Eure zarte Haut blaue Flecken bekommt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12738', 'deDE', 'Kommt herein ans Feuer und ruht Euch aus, $R. Ich hoffe, der Fischgeruch macht Euch nichts aus.', 'Kommt herein ans Feuer und ruht Euch aus, $R. Ich hoffe, der Fischgeruch macht Euch nichts aus.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12739', 'deDE', 'Durch die Täler und Gipfel des Berges Hyjal, im wechselnden Sand von Silithus, gegen die Schreckensarmeen der Legion - so haben wir gekämpft. Wir sind die namenlosen, gesichtslosen Söhne und Töchter d', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12740', 'deDE', 'Grüße! Braucht Ihr irgendetwas?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12744', 'deDE', 'Meine A.L.K. ist mehr als nur ein Ziel, auf das man Krüge wirft. Sie ist eine wertvolle Trainingsausrüstung. Wenn man A.L.K. treffen kann, kann man wahrscheinlich alles treffen!$B$BVersucht einfach mi', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12750', 'deDE', 'Alle Belohnungen werden von Hochexekutor Zürner nach vorheriger Überprüfung ausgezahlt.$B$BIndividuen mit ernsthaften Anliegen können drinnen anfragen.$B$BKEINE HAUSIERER!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12751', 'deDE', 'Wir werden die Lebenden verheeren... Ich meine natürlich, die Geißel.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12752', 'deDE', 'Hinkejoe, Ihr seid es! Ihr seid es wirklich!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12753', 'deDE', 'Was treibt Euch hier an diesen Ort, $GKleiner:Kleines;?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12755', 'deDE', '<Der Tuskarr mustert Euch misstrauisch.>$B$BBleibt nicht zu lange hier. Ihr versteht mein Volk nicht und ich will keinen Ärger.', '<Der Tuskarr mustert Euch misstrauisch.>$B$BBleibt nicht zu lange hier. Ihr versteht mein Volk nicht und ich will keinen Ärger.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12756', 'deDE', 'Der Himmel ist nicht sicher, $Gmein Freund:meine Freundin;. Seid wachsam!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12758', 'deDE', '', 'Achtet auf Eure Schritte in Yseras Garten, $GSterblicher:Sterbliche;. Die Dinge sind nicht immer so wie sie zu sein scheinen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12759', 'deDE', 'Braucht Ihr etwas? Vielleicht müsste Eure Rüstung oder Eure Waffe mal repariert werden? Hmm?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12770', 'deDE', '', 'Verzweifelte Zeiten erfordern verzweifelte Maßnahmen, $C.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12773', 'deDE', '', 'Die natürliche Schönheit dieser Hügel verdeckt die große Dunkelheit, die darunter liegt. Seid vorsichtig.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12775', 'deDE', 'Das Plumpsklo scheint momentan besetzt zu sein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12777', 'deDE', 'Mein Vater überreichte mir diese Rüstung und diese Streitaxt. Bald werde ich beides für die gute Sache nutzen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12779', 'deDE', 'Zum Glück ist das Plumpsklo gerade frei.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12780', 'deDE', '', 'Wir haben nicht mehr viel zu essen und zu trinken, sodass ich Euch leider nichts anbieten kann.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12781', 'deDE', '', 'Danke für Eure Hilfe, $N. Die Frostbeeren sind im wahrsten Sinne des Wortes die Früchte Eurer Arbeit.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12785', 'deDE', 'Nachdem ich von meinen Verwandten hier im Norden gehört hatte, kam ich her, um mehr zu erfahren. Was ich vorfand, waren sich bekriegende Stämme, die wider ihrer innersten Natur handelten.$B$BIch habe ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12787', 'deDE', 'Ja... ich spüre Vordrassils Magie... frei von jedweder wahrnehmbarer Verderbnis. Ihr müsst mir erlauben, Euch zu helfen. Das Schicksal hat mich hergebracht, vielleicht kann ich doch noch einem Zweck d', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12789', 'deDE', '', 'Wir werden Eure Tapferkeit nie vergessen, $N. Ihr werdet in Wintergarde stets willkommen sein.$B$BMöchtet Ihr in diesem Gasthaus Quartier beziehen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12793', 'deDE', 'Einst waren die Grizzlyhügel meine Heimat, Sohn des großen Ursoc, der über dieses Land herrschte.$B$BAber jetzt... jetzt bin ich dazu verurteilt, den Rest meines Lebens in dieser Höhle zu schmachten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12794', 'deDE', 'Ihr versteht nicht. Wenn ich wollte, könnte ich mich aus dieser Höhle herauskämpfen.$B$BEs ist mein Vater, Ursoc. Er ist von den Toten zurückgekehrt... aber er ist nicht derselbe.$B$BEr gab sein Leben', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12795', 'deDE', '', 'Seid leise, $C. Ich bin verkleidet und spioniere für Gallgrimm.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12796', 'deDE', 'Der Schössling, den sie anpflanzten, brachte eine Frucht hervor. Zunächst schien es zu funktionieren. Ursoc kam tatsächlich zurück und für einen kurzen Moment war ich erfüllt mit Freude.$B$BWährend er', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12797', 'deDE', 'Ich bin Kodian, Tochter des Ursoc. Ich bewache diesen einst heiligen Schrein.$B$BEs ist lange her, dass ich hier das letzte Mal einen Fremden gesehen habe.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12798', 'deDE', 'Ein Furbolg namens Tur Wutpranke. Keiner von uns.$B$BEr ist aus den südlichen Ländern gekommen. Er gehört zum Stamm der Holzschlundfeste.$B$BEr wollte Ursoc sehen. Er schien zu glauben, dass die Verde', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12804', 'deDE', '', 'Denkt Ihr, Ihr könnt mich hier rausholen, $C?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12805', 'deDE', 'Oh, gut, Ihr seid hier, um mich zu retten. Und jetzt lasst mich hier raus!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12806', 'deDE', 'Hey, $G Kumpel : Frollein;. Ich hab\' Euch hier doch schon mal gesehen.$B$BWie wär\'s, wenn Ihr mich hier rausholt?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12808', 'deDE', 'Ich weiß nicht, was passiert ist. Ich war gerade in Gallgrimm angekommen und dann wache ich mit einem Mal in diesem Käfig auf!$B$BSeid Ihr hier, um mich zu retten?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12810', 'deDE', '', 'Seid gegrüßt, $C.$B$BWie kann ich Euch helfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12811', 'deDE', 'Than Torvald Erikssons temperamentvoller Protodrache ist hier angekettet.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12812', 'deDE', 'Ja? Was ist?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12822', 'deDE', 'Ihr kennt die Kampfgeister, nicht wahr?$B$BAlle zwei Jahre nähern sie sich unseren sterblichen Gefilden. In diesen Zeiten geschieht es, dass Horde und Allianz in den Schlachtfeldern die Wappenröcke de', 'Ihr kennt die Kampfgeister, nicht wahr?$B$BAlle zwei Jahre nähern sie sich unseren sterblichen Gefilden. In diesen Zeiten geschieht es, dass Horde und Allianz in den Schlachtfeldern die Wappenröcke de', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12823', 'deDE', '', 'In diesem frostigen Ödland haben wir den Preis des Todes rasch erfahren, $N. Die Macht des Lichkönigs erstreckt sich über ganz Nordend und dringt sogar in den Boden ein! Er setzt unsere Gefallenen geg', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12824', 'deDE', 'Krenna muss Zweifel an meinen Fähigkeiten haben. Während unsere Elitekrieger ausziehen, um sich der Allianz und ihren Verbündeten unter den Fallenstellern gegenüberzustellen oder Chaos und Verwüstung ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12825', 'deDE', 'Willkommen in der Ammertannhütte, $C.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12827', 'deDE', 'Ich glaube, sie haben ihn in einen Käfig gesteckt, in der Nähe des Sägewerks.', 'Ich glaube, sie haben ihn in einen Käfig gesteckt, in der Nähe des Sägewerks.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12830', 'deDE', 'Ich glaube, er befindet sich auf der Südseite der Abtei.', 'Ich glaube, er befindet sich auf der Südseite der Abtei.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12832', 'deDE', 'Ich glaube, Ihr findet sie in der Nähe der Galgen.', 'Ich glaube, Ihr findet sie in der Nähe der Galgen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12833', 'deDE', 'Ich glaube, ich habe gesehen, wie sie ihn zu der Nordseite der Abtei gebracht haben, in der Nähe der Zielscheiben.', 'Ich glaube, ich habe gesehen, wie sie ihn zu der Nordseite der Abtei gebracht haben, in der Nähe der Zielscheiben.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12842', 'deDE', 'Was? Könnt Ihr nicht sehen, dass ich beschäftigt bin?$B$BGlaubt Ihr, diese Transmaterie bringt sich von alleine in Ordnung?!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12843', 'deDE', 'Hochlord Bolvar Fordragon kämpft für uns alle, Neuankömmling. Wäre er nicht gewesen, so würde Arthas nun auf dem Thron in Sturmwind sitzen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12844', 'deDE', 'Ich bin so nah dran, das Geheimnis dieses Erzes zu entschlüsseln. Nur ein paar Minuten noch und ich habe die Lösung!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12845', 'deDE', 'Hallöchen, $C!$B$BBereit, Euch in Richtung Venturebucht aufzumach\'n und ein paar Hordeschädel zu spalt\'n?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12846', 'deDE', 'Ja, $G mein Sohn : meine Tochter;?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12848', 'deDE', '<Der Hochabt betracht Euch einen Moment lang und lächelt dann.>$B$BSehr gut. Ich weiß einen Ort, mein Kind. Küsst einfach meinen Ring und lasst mich wissen, wenn Ihr bereit seid.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12849', 'deDE', 'Trefft mich beim Eingang der Abtei. Ich werde in Kürze dort sein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12850', 'deDE', 'Ich habe meinen Anteil daran gehabt, ganze Zivilisationen zu zerstören... Nun so tief gesunken zu sein, dass ich mich mit dem erbärmlichen Kult der Verdammten abgeben muss, widert mich an.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12851', 'deDE', '', 'Gut, aber seid dieses Mal vorsichtiger damit!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12852', 'deDE', '', 'Seid leise. Wir wollen doch nicht, dass diese Bestien auf uns aufmerksam werden!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12853', 'deDE', 'Behelligt mich nicht mit Kleinigkeiten!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12856', 'deDE', 'Könnt Ihr nicht sehen, dass ich beschäftigt bin? Die Basis wird niemals bereit für die Rückkehr des Prinzen werden!', '', 'Wenn diese verdammten Bestien nicht unsere Schiffe in Brand gesetzt hätten, wir wären schon längst zu Hause. Und jetzt lasst mich in Ruhe, damit ich weiterarbeiten kann!', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12857', 'deDE', 'Hier gilt das Wort des Argentumkreuzzugs, $Gmein Freund:meine Liebe;. Wir konnten ja nicht zulassen, dass Ihr all den Ruhm einheimst!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12858', 'deDE', 'Diese verdammten Bauern müssen schneller arbeiten! Ich will heim nach Eisenschmiede. Ich vermisse meine Frau und meine Kinder ganz furchtbar.', '', 'Der Hauptmann sagt, dass wir uns auf den Heimweg machen, sobald Prinz Arthas zurückkommt.', '', 'Ich weiß nicht, was den jungen Prinzen so lange aufhält, aber ich glaube nicht, dass wir noch viel länger durchhalten können. Die Kälte macht einigen Soldaten sehr zu schaffen.', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12859', 'deDE', 'Als wir sahen, dass diese wilden Bestien die Schiffe abgebrannt hatten, sanken uns die Herzen. Niemals zuvor habe ich mich so hilflos gefühlt...', '', 'Wut. So kann man meine Gefühle dem ganzen Schlamassel gegenüber zusammenfassen. Es war eine Knochenarbeit, uns durch die Wälder zum Strand durchzukämpfen, nur um dann festzustellen, dass die einzige M', '', 'Trotz all des Elends, das wir in dieser Ödnis erleiden mussten, glaube ich, dass der junge Prinz uns zum Sieg führen wird. Ich bete jede Nacht, dass er in Sicherheit ist.', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12860', 'deDE', '<Der vergessene Fußsoldat schüttelt den Kopf.>$B$BWir können so nicht weitermachen, $Gmein Freund:meine Freundin;. Wenn Prinz Arthas nicht bald zurückkehrt, sind wir alle verloren.', '', 'Wir werden diese Schiffe niemals rechtzeitig klarmachen. Der Winter kommt und mit ihm unser Untergang. Ich fürchte, der junge Prinz hat uns alle verdammt.', '', 'Arthas und Muradin sind schon sehr lange weg. Ich hoffe, sie sind nicht in Gefahr.', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12862', 'deDE', 'Wo kann ich Euch heute hinschicken?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12863', 'deDE', 'Es geht nichts über einen guten Verein.', 'Es geht nichts über einen guten Verein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12864', 'deDE', 'He da, $GKamerad:Kameradin;! Wie ich sehe, habt Ihr da ein paar Braufestmarken. Zufälligerweise habe ich noch immer Braufestgegenstände zu verkaufen.', 'He da, $GKamerad:Kameradin;! Wie ich sehe, habt Ihr da ein paar Braufestmarken. Zufälligerweise habe ich noch immer Braufestgegenstände zu verkaufen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12865', 'deDE', 'Tachchen, $R!$B$BWenn Ihr nach einem Schredder sucht, auf dem Ihr fahren könnt, so müsst Ihr Euch in Richtung der Hügel wenden.$B$BDieser hier ist außer Betrieb.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12867', 'deDE', 'Ein gutes Bier ist ein starkes Bier.', 'Ein gutes Bier ist ein starkes Bier.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12868', 'deDE', 'Nun ja, bevor die Kämpfe ausbrachen, haben wir sie hergerichtet, um die heimischen Bäume abzuernten.$B$BIn letzter Zeit sind sie jedoch gegen die Horde eingesetzt worden. Es hat sich nämlich herausges', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12869', 'deDE', 'Es ist toll, Mann, beim \"Bier des Monats e.V.\" Mitglied zu sein.', 'Es ist toll, Mann, beim \"Bier des Monats e.V.\" Mitglied zu sein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12871', 'deDE', 'Mein Bruder, Synipus, hat jede Menge davon. Geht in Richtung Norden, zum Holzfällerposten Blauhimmel, und sprecht mit ihm.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12873', 'deDE', 'Mit genug Losen könnt Ihr dem \"Bier des Monats e.V.\" beitreten. Werdet Mitglied des Vereins und feiert das ganze Jahr lang Braufest!$B$BJeden Monat wird Euch per Post ein neues Probebier zugeschickt. ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12878', 'deDE', 'Ihr habt noch nie vom Bier des Monats e.V. gehört? Diese Typen stehen auf Party. Wenn es nach denen ginge, würde das Braufest nie zu Ende gehen! Letztes Jahr kam es mir ein paar Mal so vor, als wäre d', 'Ihr habt noch nie vom Bier des Monats e.V. gehört? Diese Typen stehen auf Party. Wenn es nach denen ginge, würde das Braufest nie zu Ende gehen! Letztes Jahr kam es mir ein paar Mal so vor, als wäre d', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12880', 'deDE', 'Ihr habt noch nie vom Bier des Monats e.V. gehört? Das ist ein Haufen richtig süßer Typen, die das ganze Jahr lang das Braufest feiern wollen! In der Vergangenheit haben sie versucht, das Braufest wie', 'Ihr habt noch nie vom Bier des Monats e.V. gehört? Das ist ein Haufen richtig süßer Typen, die das ganze Jahr lang das Braufest feiern wollen! In der Vergangenheit haben sie versucht, das Braufest wie', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12881', 'deDE', 'Die Untoten erheben sich aus unserer Gruft!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12882', 'deDE', 'Ihr habt es geschafft, $G mein Junge:mein Mädchen;! Ich hatte ja schon gedacht, Ihr wärt erledigt!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12883', 'deDE', 'Was tut Ihr denn noch immer hier? Geht zurück in die Stadt!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12887', 'deDE', 'Königin Alexstrasza hat mir die Verteidigung des Wyrmruhtempels unterstellt.$B$BWie Ihr wisst, hat Malygos seine Truppen den nahegelegenen Leylinienanker zerstören lassen. Die Streitkräfte des Drachen', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12895', 'deDE', 'Hey, $C, habt Ihr das gesehen? Da draußen, in der Nähe der Wasseroberfläche... $B$BIhr habt das auch gesehen, oder?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12896', 'deDE', 'Den wa-?? Nun, ich bezeichne es als unbekanntes Schwimmobjekt. Und da wo ich herkomme, bestreiten die Meisten ihre Existenz gänzlich.$B$BAber ich weiß es besser! Ich habe sie schon von Nahem gesehen! ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12897', 'deDE', 'Lasst Euch nicht zum Narren halten, $R. Sie sind sehr schwer zu fassen. Im einen Moment sind sie hier, im nächsten weg - es ist einfach unerklärlich!$B$BUnter uns, ich habe tatsächlich schon mal ein S', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12898', 'deDE', 'Was wisst Ihr schon...$B$BManche sagen, sie wären einfach nur Beobachter, manche denken, sie würden in das Leben im Meer eingreifen und wiederum andere glauben, sie wären hier, um Azeroth zu erobern!$', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12899', 'deDE', 'Grüße, $R.$B$BIch habe euch erwartet.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12900', 'deDE', 'Wohlan denn, gehen wir!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12904', 'deDE', 'Die Greifen aus Nordends Gipfeln sind die robustesten ihrer Art.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12905', 'deDE', 'Die Greifenreiter stehen nur 10 Schritte entfernt von hier, $G mein Freund:meine Freundin;. Sie werden Euch zu Thorsons Posten bringen. Kommt in die Gänge!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12906', 'deDE', '', 'Denkt nicht einmal für einen Augenblick, dass Ihr durch Eure früheren Leistungen irgendein Anrecht auf etwas hättet, $N. Dem ist nicht so.$B$BWenn Ihr meinen Respekt wollt, müsst Ihr ihn Euch verdiene', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12909', 'deDE', 'Hallo. Knappe Walter und ich sind verantwortlich dafür, uns um Graumähne hier zu kümmern, und um den Rest der Reittiere auch.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12912', 'deDE', 'Willkommen in Camp Oneqwah.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12913', 'deDE', 'A.L.K. ist mehr als nur ein Wurfziel für Bierkrüge, es ist auch ein wertvolles Trainingsmittel. Wenn Ihr A.L.K. treffen könnt, könnt Ihr womöglich alles treffen!$B$BPasst einfach auf, dass Ihr mir die', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12915', 'deDE', '', 'Belgaristrasz sagt die Wahrheit; die Zeit läuft ab!$B$BIch kann Euch die Macht geben, einen Drachen des grünen Drachenschwarms herbeizurufen. Sprecht mit Belgaristrasz oder Eternos, falls Ihr es vorzi', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12916', 'deDE', 'Varos Wolkenwanderer und seine Ringwächter schützen den zweiten Ring. Eure Drachen können sich mit den Ringwächtern in jeder Hinsicht messen, doch Varos befindet sich hinter einem undurchdringlichen S', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12917', 'deDE', 'Belgaristrasz sagt die Wahrheit; die Zeit läuft ab!$B$BIch kann Euch die Macht geben, einen Drachen des Bronzeschwarms herbeizurufen. Sprecht mit Belgaristrasz oder Verdisa, falls Ihr es vorzieht, die', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12926', 'deDE', 'Die meisten meiner Brüder verachten die \"barbarische\" Art der Horde. Dazu zählte ich auch einst. Als ich zu Anfang an diesen etwas feuchtwarmen und... geruchvollen Ort beordert wurde, verfluchte ich m', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12927', 'deDE', 'Die meisten meiner Brüder verachten durch die \"barbarische\" Art der Horde. Dazu zählte ich auch einst. Als ich zu Anfang an diesen etwas feuchtwarmen und... geruchvollen Ort beordert wurde, verfluchte', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12929', 'deDE', 'Hm? Oh. Vergebt mir, wenn ich abgelenkt scheine; ich fürchte um die Männer von Nordwacht. Ich hätte ein paar meiner Schüler zur Hilfe entsenden sollen. Nun, wie kann ich Euch behilflich sein?', 'Hm? Oh. Vergebt mir, wenn ich abgelenkt scheine; ich fürchte um die Männer von Nordwacht. Ich hätte ein paar meiner Schüler zur Hilfe entsenden sollen. Nun, wie kann ich Euch behilflich sein?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12933', 'deDE', '', 'Sinu a\'manore. Falls Ihr danach strebt, ein Portal nach Theramore zu öffnen, kann ich es Euch zeigen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12934', 'deDE', 'Seitdem das Braufest zum ersten Mal stattgefunden hat, haben sich ein paar Dinge verändert. Wir geben nun keine Lose mehr aus, sondern Braufestbiermarken. Falls Ihr alte Lose haben solltet, dann kann ', 'Seitdem das Braufest zum ersten Mal stattgefunden hat, haben sich ein paar Dinge verändert. Wir geben nun keine Lose mehr aus, sondern Braufestbiermarken. Falls Ihr alte Lose haben solltet, dann kann ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12937', 'deDE', '', 'Wollt Ihr reisen, $R?$B$BOder habt Ihr etwas anderes im Sinn?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12938', 'deDE', 'Nun hört mal zu. Wenn Ihr nach Antworten sucht, dann geht nichts über eine gute Explosion - je größer, je besser!$B$BAber damit ist es wahrscheinlich fürs Erste genug der Fragen, $R.$B$BMeine Sprengkö', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12939', 'deDE', '', 'Seid willkommen. Ihr trefft zur rechten Zeit ein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12940', 'deDE', 'Thel\'zan muss sterben, und sei es nur, damit das permanente Gejammer endlich aufhört!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12941', 'deDE', 'Offizieller Aushang der Bürgerschaft und der Verwaltung Wintergardes.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12942', 'deDE', '', 'Macht Euch die Anwesenheit eines Schwarzen Drachen nervös?$B$BGut. Das sollte sie auch. Diesen Drachen geht es ebenso, aber sie klammern sich an ihren uralten Tugenden fest und würden es nicht wagen, ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12943', 'deDE', 'Hört zu, $R.$B$BWenn Ihr einen Schredder sucht, um darauf zu reiten, so müsst Ihr Euch in die Hügel nördlich von hier begeben.$B$BDieser hier ist außer Betrieb.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12944', 'deDE', 'Zunächst haben wir sie repariert, um etwas zu haben, mit dem man sich die Wälder hier zunutze machen kann. Als dann aber die Allianz hier auftauchte und die Kämpfe ausbrachen, haben wir sie als Kriegs', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12945', 'deDE', 'Grekk hat sie.$B$BVielleicht gibt er Euch einen, wenn Ihr ihm aushelft. Das nächste Mal, wenn Ihr im Holzfällerposten Blauhimmel seid, dann sprecht mit ihm.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12946', 'deDE', 'Ja?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12949', 'deDE', '', 'Der ewige Drachenschwarm versucht, das Schicksal von Prinz Arthas Menethil zu verändern. Ihr kennt ihn als den Lichkönig, aber hier und jetzt war er noch ein Prinz von Lordaeron, der das Beste für sei', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12950', 'deDE', '', 'Arthas gab den Befehl, Stratholme zu säubern und jeden Menschen innerhalb der Stadtmauern zu töten. Er fand Beweise dafür, dass die Geißel die Stadt mit ihrer heimtückischen Seuche infiziert hatte, un', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12952', 'deDE', '', 'Die Ewigen planen etwas ungewöhnlich Hinterhältiges. Sie versuchen, den Beweis zu verstecken, der dazu führen wird, dass Arthas Stratholme auslöscht. Ihre Agenten haben die Lieferungen aus verseuchtem', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12953', 'deDE', '', 'Ich glaube nicht, dass wir es uns leisten können, diese Möglichkeit, uns beim roten Drachenschwarm beliebt zu machen, ungenutzt zu lassen. Bei den bevorstehenden Schlachten ist ihre Unterstützung unbe', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12954', 'deDE', 'In längst vergangenen Jahren bediente sich der Kriegsherr Nekros Schädelbrecher eines mächtigen Artefakts, um Alexstrasza und einen Großteil des roten Drachenschwarms zu versklaven. Zuluhed und Nekros', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12955', 'deDE', 'Die Allianz hat diese abgelegene Hütte einige Zeit unterhalten. Sie wurde ab und zu von unseren Jägern und Förstern genutzt.$B$BNun, da Krieg herrscht, könnt Ihr aber sicher sein, dass das Militär hie', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12958', 'deDE', '<Der Kommandant unterbricht seinen Unterricht und dreht sich um, während sein abschätzender und bewertender Blick auf Euch ruht.>$B$BJemanden wie Euch kann ich gebrauchen, $C.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12960', 'deDE', 'Ja, $C, was gibt\'s?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12961', 'deDE', 'Es ist gut, Euch wiederzusehen, $N.$B$BKommt Ihr, um Eure Kenntnisse in Geißeltaktik aufzufrischen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12963', 'deDE', '', 'Liebling, für diese Schuhe könnte man sterben! Das meine ich wörtlich. Wen hast du dafür umgebracht?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12965', 'deDE', 'Warte auf Befehl!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12966', 'deDE', 'Willkommen in Granitquell, $Gmein Freund:meine Freundin;.$B$BSollte es Euch gelingen, einen der Eistrolle lebendig einzufangen, so gebt mir auf jeden Fall Bescheid. Wir zahlen gut für sie.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12968', 'deDE', 'He, wie ich sehe, habt Ihr ein paar Braufestbiermarken. Wenn Ihr wollt, dann kann ich Euch noch ein paar Braufestsachen verkaufen.', 'He, wie ich sehe, habt Ihr ein paar Braufestbiermarken. Wenn Ihr wollt, dann kann ich Euch noch ein paar Braufestsachen verkaufen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12976', 'deDE', '', '$N, schön Euch zu sehen!$B$BOder ist dies das erste Mal, dass wir uns begegnen?$B$BIch war bereits an so vielen Orten und in so vielen Zeiten, dass ich manchmal ein wenig den Überblick verliere.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12982', 'deDE', 'Ich verließ einst einen Krieg, um mich um die Probleme zu Hause in Westfall zu kümmern. Es scheint Ewigkeiten her zu sein. Dem Licht sei Dank, dass die Defias nicht länger eine Bedrohung sind.$B$BWir ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12990', 'deDE', 'Meine Kriegsmagier sind auf dem Weg und werden in Kürze eintreffen.$B$BSprecht mich erneut an, sobald sie zurückgekehrt sind.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12991', 'deDE', 'Verzeiht mir, $R, aber ich bin im Dienst und darf nicht gestört werden.$B$BBitte entschuldigt mich.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12992', 'deDE', '', 'Gute Arbeit! Arthas weiß jetzt vom verseuchten Korn in Stratholme und er wird mit dem Ausmerzen beginnen. Aber irgendetwas stimmt noch nicht. Ich spüre eine fremde Präsenz neben uns in dieser Zeitlini', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12993', 'deDE', '', 'Ich weiß es nicht und das ist beunruhigend. Ich tue, was ich kann, um die Unendlichen zu finden. Ihr müsst jetzt in Arthas\' Nähe bleiben, indem Ihr Euch seiner Armee anschließt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12994', 'deDE', '', 'Ihr müsst keine Anwerbungsformulare oder etwas Derartiges unterschreiben, aber Ihr müsst an seiner Seite kämpfen. Ihr müsst dafür sorgen, dass Arthas Stratholme auslöscht und Mal\'ganis besiegt. Ohne U', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12995', 'deDE', '', 'Viel Glück und passt auf Euch auf. Da kommen Uther und Jaina.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12998', 'deDE', '<Drakuru streckt seine blutende Hand durch die Gitterstäbe und schaut Euch erwartungsvoll an.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12999', 'deDE', 'Es gab Zeiten, da hätt\' ich Euer Herz rausgeriss\'n, nur um Euren Gesichtsausdruck zu seh\'n...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13001', 'deDE', 'Ey, Mann!$B$BSchön, dich wiederzuseh\'n... $N, richtig? Ja, ja, natürlich.$B$BDu und ich, wir werd\'n gute Freunde werden. Drakuru weiß solche Sach\'n.$B$B<Langsam zieht sich ein Grinsen über das bemalte', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13003', 'deDE', 'Von diesem Throne aus erteile ich die Befehle Höllschreis. Sein Wort ist Leben und sein Wort ist Tod.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13004', 'deDE', '', 'Elune wacht über Euch, $R.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13009', 'deDE', '', 'Dies ist die einzige Möglichkeit, dass Harry überhaupt mit mir spricht! Ich muss diese blöde Maske die ganze Zeit aufbehalten...', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13011', 'deDE', 'Ey, Mann!$B$BEs ist nicht das, wonach es aussehen tut. Na ja, vielleicht doch...$B$BEs is\' kompliziert.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13012', 'deDE', 'Hallo, Mann! Kenn ich dich?$B$BAhh, ja, Prigmon vergisst nie\'n Gesicht. $B$BIhr... Verfolgste mich, Mann? Weil, was auch immer es war, Prigmon hat\'s nich\' gemacht!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13018', 'deDE', 'Seid gegrüßt, $R.$B$BWenn Ihr daran interessiert sein solltet, bei der Verteidigung des Tempels zu helfen, dann sprecht mit Lord Afrasastrasz im Inneren.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13019', 'deDE', 'Willkommen in meinem Lager, $R.$B$BEs mag nich\' nach viel aussehen, aber es ist mein Zuhause.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13022', 'deDE', 'Wie kann ich Euch helfen, mein Kind?', 'Wie kann ich Euch helfen, mein Kind?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13023', 'deDE', '', 'Mr. Floppy und ich sind spazieren gegangen, aber dann haben wir uns verlaufen und ich habe ganz doll Angst gekriegt, weil da waren diese Wölfe, wisst Ihr, und sie haben uns gejagt, aber dann habe ich ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13025', 'deDE', 'Ihr seid willkommen im Wyrmruhtempel, $R. Verzeiht die Aufruhr draußen; Malygos\' kürzliche Handlungen haben uns in einen Belagerungszustand versetzt. Wenn Ihr dennoch hier rasten wollt, so steht Euch ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13030', 'deDE', 'Seid gegrüßt, $C!$B$BIch hoffe, Ihr habt nichts gegen ein wenig harte Arbeit. Wir können auf jeden Fall ein wenig Unterstützung gebrauchen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13031', 'deDE', '', 'Der Wind und die Bäume berichten von unsichtbaren Problemen. Das Land wirkt gesund, aber hier ist mehr, als das bloße Auge zu sehen vermag.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13033', 'deDE', 'Felle für die Horde, Mann! Nur das Beste für unsere Truppen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13034', 'deDE', 'Etwas Wegzehrung? Oder vielleicht eine Erfrischung, die Ihr bei den Kämpfen genießen könnt?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13035', 'deDE', 'Die Feinde kommen von allen Seiten näher und alles, worüber die Ältesten reden, ist, diesen Posten aufzugeben. Ich sage, wir bleiben und kämpfen wie die Krieger, die wir sind.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13042', 'deDE', 'Ich nenne sie die \'Geist von Gnomeregan\'. Ist sie nicht eine echte Schönheit? Ohne Eure Hilfe wäre sie nie restauriert worden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13043', 'deDE', 'Willkommen in der Argentumwache, $C.$B$BFühlt Euch wie zu Hause, doch macht es Euch nicht zu gemütlich. Wir sind noch nicht in Sicherheit - ganz im Gegenteil.$B$BWenn wir es in einem Stück hier raussc', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13044', 'deDE', 'Der Schreckenskapitän DeMeza ist in der Beutebucht eingelaufen und hat den Piratentag ausgerufen!$B$BJeder, der Mut genug hat, mit ihr zu trinken, wird zu einem ehrenamtlichen Crewmitglied ernannt.', 'Der Schreckenskapitän DeMeza ist in der Beutebucht eingelaufen und hat den Piratentag ausgerufen!$B$BJeder, der Mut genug hat, mit ihr zu trinken, wird zu einem ehrenamtlichen Crewmitglied ernannt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13045', 'deDE', 'Jetzt haben wir den Salat.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13046', 'deDE', 'Das Land ist besudelt durch die Gegenwart der Geißel auf der einen Seite und die Rituale der Drakkari auf der anderen.$B$BWir stehen voller Entschlossenheit zwischen den beiden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13047', 'deDE', 'Dem Licht sei Dank für Euch!', 'Dem Licht sei Dank für Euch!', 'He dort, $C. Wir befinden uns hier mitten im Kampf.', 'He dort, $C. Wir befinden uns hier mitten im Kampf.', 'Was treibt Ihr hier draußen?', 'Was treibt Ihr hier draußen?', 'Seid Ihr nicht $N? Ich hörte, Ihr wärt tot.', 'Seid Ihr nicht $N? Ich hörte, Ihr wärt tot.', 'Beeilt Euch... Die Kämpfe hier sind übel. Ich muss auf der Hut bleiben.', 'Beeilt Euch... Die Kämpfe hier sind übel. Ich muss auf der Hut bleiben.', 'Seid Ihr ein neuer Rekrut? Das ist nicht die Standardrüstung.', 'Seid Ihr ein neuer Rekrut? Das ist nicht die Standardrüstung.', 'Ah, gut, Verstärkung!', 'Ah, gut, Verstärkung!', 'Ihr habt eine Nachricht für mich?', 'Ihr habt eine Nachricht für mich?');
INSERT INTO `npc_text_localized` VALUES ('13048', 'deDE', 'Ein bisschen haariger hier draußen, als Ihr es erwartet habt, $R?$B$BKann nicht behaupten, dass mich das überraschen würde. Das höre ich von fast jedem hier, der so weit gekommen ist.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13050', 'deDE', 'Ich bin Ubungo. Ich vertrete die Zandalari.$B$BSchlimme Dinge gehen vor sich, $R. Die Drakkari werden für ihren Untergang selbst verantwortlich sein, mit dem, was sie vorhaben.$B$BManche Trolle werden', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13057', 'deDE', 'Hapana grunzt einfach nur und winkt Euch in Richtung Schreckenskapitänin DeMeza.$B$BUnter dem Fell eines wirklich kräftigen Armes seht Ihr das Wort \"MAGATHA\" als Tätowierung in einem von einem Pfeil d', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13059', 'deDE', '', 'Ihr habt ganz schöne Kartoffeln, Euch hier hoch zu trauen, $GFreundchen:Missy;. Wir feiern Piratentag, wisst Ihr... und bis die Sonne am Morgen wieder aufgeht gehört Beutebucht MIR.$B$BAlso! Was sagt ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13060', 'deDE', 'Oh... ich fühl\' mich ein wenig groggy...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13061', 'deDE', 'Geht mir nicht auf den Geist, $C! Ich hab hier zwei ganze Kessel mit blubberndem Gebräu und muss sicherstellen, dass sie richtig schmecken!$B$BFalls ich das nicht schaffe, wird Meister Coren Düsterbrä', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13063', 'deDE', 'Das wird immer besser! Es wärmt wirklich den Magen! Und, $N, Ihr seid wirklich ein heißer Feger!', '', 'Ja, $Gmein Herr:meine Dame;, das ist doch mal was zu trinken! Was Anständiges, in der Tat!', '', 'Singen wir doch mal was!$B$BHau wech,$B$Bhau drauf!$BWir Zwerge haben\'s gut!$B$BHaha! Ich mag Euch, $N!', '', 'Nun, wo war ich stehen geblieben? Ach ja,... Also sag\' ich zu ihr: \"Aber ich brauche dich! Ich brauche dich doch!\"... Und sie hat nur gelacht und kroch davon! Ehrlich wahr, $N. Naga können so grausam ', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13064', 'deDE', 'Das ist echt guter Stoff! Jedoch etwas zu schwach. Ich denke, da sollte ich noch mehr Knoblauchatem zugeben...', '', 'Das Zeug ist echt gut! Aber noch ein wenig schal, finde ich. Ein paar matschige Basiliskenaugen würden es abrunden...', '', 'Nicht schlecht, nicht schlecht... Doch da fehlt noch etwas Würze! Wie wär\'s mit etwas Skorpidgift? Ja! Das wird\'s richten!', '', 'Es wird langsam! Aber da fehlt noch der Kick. Ja, Ihr habt es erraten... Waschen wir mal die Socken von gestern im Kessel... Mmmmh, lecker!', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13065', 'deDE', '', 'Ahoi! Heute ist Piratentag, wisst Ihr... und bis die Sonne am Morgen wieder aufgeht gehört Beutebucht MIR.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13068', 'deDE', 'Wahrhaftig, die Schrecken hier übertreffen noch diejenigen, die wir in den Pestländern ertragen mussten.$B$BDie Geißel ist mächtiger denn je zuvor.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13069', 'deDE', 'Ich kann nicht mehr...$B$BSie töten dich und dann essen sie dich auf! Und das nur, wenn du Glück hast!', 'Ich kann nicht mehr...$B$BSie töten dich und dann essen sie dich auf! Und das nur, wenn du Glück hast!', 'Ihr müsst mir hier raushelfen, $R!$B$BIch will nicht so enden, wie die!', 'Ihr müsst mir hier raushelfen, $R!$B$BIch will nicht so enden, wie die!', 'Ich kann weder essen noch schlafen, seit ich hier bin.$B$BIch bin einfach nicht gemacht für den Kreuzzug...', 'Ich kann weder essen noch schlafen, seit ich hier bin.$B$BIch bin einfach nicht gemacht für den Kreuzzug...', '<Schluchz> Mein Onkel hat bei der Argentumdämmerung gedient.$B$BWie war es ihm möglich, sich solch grässlichen Dingen zu stellen?', '<Schluchz> Mein Onkel hat bei der Argentumdämmerung gedient.$B$BWie war es ihm möglich, sich solch grässlichen Dingen zu stellen?', 'Jedes Mal, wenn ich meine Augen schließe, sehe ich, wie die Geißel mich umstellt!$B$BIch muss hier raus...', 'Jedes Mal, wenn ich meine Augen schließe, sehe ich, wie die Geißel mich umstellt!$B$BIch muss hier raus...', 'Ich werde hier sterben... Ich weiß es einfach.', 'Ich werde hier sterben... Ich weiß es einfach.', 'Wir sind verlooooren!', 'Wir sind verlooooren!', 'Verschwindet von hier, solange Ihr noch könnt, $C!$B$BWir haben einfach keine Chance, zu gewinnen...', 'Verschwindet von hier, solange Ihr noch könnt, $C!$B$BWir haben einfach keine Chance, zu gewinnen...');
INSERT INTO `npc_text_localized` VALUES ('13070', 'deDE', '', 'Das ist ja ein Schlamassel.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13074', 'deDE', 'Sonst würde ich das nie machen, aber... ich habe Durst und ich hasse es, allein zu trinken.$B$BHoch die Krüge!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13076', 'deDE', 'Seid Ihr bereit, zu tun, was getan werden muss, um Lordaeron zu beschützen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13077', 'deDE', 'BUMM!$B$BDas ist das Letzte, woran ich mich erinnere...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13078', 'deDE', '<Seufz>...$B$BEin wenig Hilfe, vielleicht?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13079', 'deDE', 'Beachtet mich gar nicht. Ich bin nur ein alter Mann und warte auf einen alten... Freund.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13080', 'deDE', '', 'Ohhhh, der Schmerz ist... unerträglich...', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13081', 'deDE', '', 'Schnell, $R.$B$BMeine Zeit läuft ab.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13082', 'deDE', 'Beeilt Euch, $G Bruder : Schwester;. Ich laufe aus...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13083', 'deDE', '<Seufz>... Glaubt Ihr, wir könnten uns hinaus bewegen, $C?$B$BIch verliere schnell Flüssigkeit!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13089', 'deDE', 'Was ist los? Könnt Ihr nicht sehen, dass ich beschäftigt bin?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13092', 'deDE', 'Auf wen seht Ihr denn herab?$B$BIch muss Euch mitteilen, dass die Stößelpots eine berühmte Familie mit einer langen Tradition im Stößeln sind.$B$BJa, da staunt Ihr!$B$BEs gibt in ganz Azeroth keine be', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13093', 'deDE', '', 'Hört zu, Ihr erzählt Schweitzer und Roitau, dass wir hier alles unter Kontrolle haben. Sie müssen nicht... was?$B$BSie haben Euch nicht geschickt? Warum verschwendet Ihr dann meine Zeit?$B$BIch muss a', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13094', 'deDE', 'He, könnt Ihr nicht sehen, dass ich hier beschäftigt bin?!$B$BWollt Ihr die Vorstellung sehen? Die Band spielt jeden Abend um acht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13097', 'deDE', 'Grüße, $R.$B$BIch fürchte, Ihr müsst mich entschuldigen, da ich im Moment furchtbar beschäftigt bin.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13098', 'deDE', 'Die Zeit drängt, $N. Seid Ihr bereit, Euch einem Test zu unterziehen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13100', 'deDE', 'Ich bin überrascht, an einem Ort wie diesem auf andere Abenteurer zu treffen! Hätte ich gewusst, dass Ihr kommt, dann wäre ich ja einfach mit Euch mitgekommen, anstatt diesen infernalischen Gnomenappa', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13101', 'deDE', 'Schaut es Euch an, Kameraden! Was für ein wirklich wunderbarer Anblick! Wenn doch nur meine teuren Brüder jetzt hier sein könnten. Sie würden nicht glauben, was für eine Entdeckung dies sein wird! Das', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13117', 'deDE', 'Das hat uns wirklich noch gefehlt, dass diese verrückten Drakkaritrolle eine Nekropole der Geißel in unserem Hinterhof abschießen.$B$BWie haben sie das überhaupt geschafft?!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13118', 'deDE', '', 'Uh, ich hätte mich niemals freiwillig als Spezialist melden sollen. Sie verlangen immer das Unmögliche von uns!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13120', 'deDE', '<Der gefangene Krokilisk knurrt leise, als Ihr Euch nähert.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13121', 'deDE', '', 'Ein toller Tag für einen Streifzug im Feld des Kampfes, meint Ihr nicht, $Gmein Herr:meine Dame;?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13124', 'deDE', '<Das verletzte Orakel der Regenrufer stöhnt vor Schmerzen.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13125', 'deDE', 'Ihr müsst einer der tapferen Soldaten sein, die Mal\'Ganis\' Geißelstreitkräfte bekämpft haben. Gut gemacht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13126', 'deDE', 'In der Tat. Mal\'ganis befehligt die Geißel vom Kreuzzüglerplatz aus, aber die Tore, die vom Eingang der Stadt dorthin führen, sind verschlossen. Er hat ja keine Ahnung, dass eine kleine Streitmacht de', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13132', 'deDE', '', 'Falls Ihr irgendwo hinwollt - ich kann Euch hinbringen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13135', 'deDE', 'Lasst mich hier raus, $R!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13137', 'deDE', 'Ekelhafter Großzungenfreund, du nicht hierher gehören. Gehen weg von Land von Wildherzen, oder ich magische Dings rufe, um dich zu töten!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13138', 'deDE', 'Wildherzen niemals Freunde werden mit Großzungen. Du dumm, zurückkommen hierher Verräterdings!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13139', 'deDE', '', 'Ihr werdet meine Erlösung sein.$B$BUnd doch könntet Ihr auch mein Untergang sein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13147', 'deDE', 'Habt ihr jemals Drachen wie die hier gesehen? Perfekt zum jagen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13155', 'deDE', 'Tut mir leid, unser Bier ist nur für Mitglieder des Bier des Monats e.V.', 'Tut mir leid, unser Bier ist nur für Mitglieder des Bier des Monats e.V.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13156', 'deDE', 'Dieser Posten ist sicher, $R.$B$BDiese Gegend ist seit einiger Zeit frei von Feinden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13157', 'deDE', '', 'Danke, dass Ihr mich befreit habt, $R.$B$BMir ist noch ein wenig schummrig von dem Gift. Ich glaube, ich bleibe hier, bis meine Kräfte zurückgekehrt sind.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13172', 'deDE', '', 'Durch die Kraft des Unheiligen beherrschen wir den Tod selbst.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13173', 'deDE', 'Das Blut ist Leben... Und so einfach es ist, Ebbe und Flut des Blutes zu kontrollieren, so einfach ist die Kontrolle unserer Feinde.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13174', 'deDE', 'Wir sind das, was der kalte Wind mit sich trägt. Das eisige Frösteln der Verzweiflung... Die kalte Umarmung des Todes...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13176', 'deDE', 'Es betrübt uns alle, zu sehen, dass die Drakkari sich auf diese Weise selbst zerstören.$B$BDie Macht, die sie dadurch gewinnen, dass sie Naturgottheiten töten, ist vergänglich.$B$BWas kurzfristig gewo', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13177', 'deDE', 'Sammelt Euch rasch, wir müssen weiter.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13179', 'deDE', 'Es wird einfach nicht leichter. Stratholme steht in Flammen. Wir müssen der Geißel und dem Feuer trotzen, um zu Mal\'Ganis zu gelangen. Haltet Euch bereit.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13180', 'deDE', '', 'Zim\'Torga ist in der Lage, uns ein gewisses Maß an Schutz zu gewähren, doch sie ist stark geschwächt durch die Gegenwart der Geißel und die Taten der Drakkari.$B$BEs ist an mir und Ahunae, unsere rest', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13234', 'deDE', 'Ahunaes Axt wird den Drakkari schon ein Ende machen.$B$BUnd Ihr kümmert Euch um die, die sich an meiner Axt vorbei stehlen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13237', 'deDE', 'Der schwindlerische Magierlord Urom schützt den dritten Ring. Es wird so aussehen, als sei er allein und wehrlos, doch lasst Euch durch den Schein nicht trügen! Urom ist ein mächtiger Beschwörer, der ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13238', 'deDE', 'Eure größte Herausforderung liegt vor Euch. Leywächter Eregos ist ein blauer Drache mit gewaltigen Kräften. Ihr könnt ihn über dem höchsten Ring fliegen sehen.$B$BDie ganze Macht Eurer Drachen steht z', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13240', 'deDE', 'Der Bronzeschwarm steht in Eurer Schuld.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13254', 'deDE', 'Rubindrachen zeichnen sich durch ihre Fähigkeit aus, Schaden abzuschwächen und ihre Verbündeten zu schützen.$B$BSie sind in der Lage, Sengende Rache zu wirken und dadurch flüssiges Feuer zu spucken, w', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13256', 'deDE', 'Bernsteindrachen sind Meister darin, die Zeit zu ihrem Vorteil zu manipulieren.$B$BDurch das Wirken von Schocklanze ist es ihnen möglich, den Verlauf der Zeit um ein feindliches Ziel zu stören, was be', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13257', 'deDE', 'Ein Bernsteindrache bei voller Kraft kann einen Temporalriss wirken, der eine Blase von verdichteter Zeit um einen Feind hervorruft. Zauber, die diese Blase passieren, verursachen doppelten Schaden un', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13258', 'deDE', '', 'Smaragddrachen sind Meister darin, sich selbst zu heilen, während sie ihre Feinde vergiften.$B$BDurch den Einsatz von entziehendem Gift gelingt es ihnen, ihren Feinden Gesundheit zu stehlen. Wiederhol', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13259', 'deDE', '', 'Ein Smaragddrache in voller Kraft kann Traumkanalisierung wirken, welche die Lebensenergie des Drachen auf ein befreundetes Ziel transferiert, um es zu heilen. Entziehendes Gift kann benutzt werden, u', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13261', 'deDE', 'Willkommen im Lager, $C.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13262', 'deDE', 'Was braucht Ihr von Corvus?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13265', 'deDE', 'Denk darüber nach, was du heute geseh\'n hast, $N.$B$BDie Zukunft steht schon fest. Dir bleibt nur, dich zu entscheid\'n, ob du\'n Teil davon sein willst, Mann.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13267', 'deDE', 'Die mächtigsten Agenten Malygos\' beschützen die oberen Ringe. Allein habt Ihr keine Chance, sie zu besiegen. Wir können Euch die Macht geben, Drachen aus unseren Drachenschwärmen herbeizurufen. Auf de', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13268', 'deDE', 'Belgaristrasz sagt die Wahrheit; die Zeit läuft ab!$B$BDie ganze Macht Eurer Drachen steht zur Verfügung. Nutzt ihre Kräfte, um Eregos zu vernichten!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13269', 'deDE', '', 'Belgaristrasz sagt die Wahrheit; die Situation ist wirklich ernst.$B$BDie ganze Macht Eurer Drachen steht zur Verfügung. Nutzt ihre Kräfte, um Eregos zu vernichten!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13270', 'deDE', 'Niemand kennt die Geißel besser als wir Todesritter.$B$BGlaubt mir, $R, es gibt nichts effektiveres, als Feuer mit Feuer zu bekämpfen...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13271', 'deDE', 'Sei nicht so streng mit dem alten Narsch. Er ist nicht mehr so jung und belastbar wie einst.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13282', 'deDE', 'Obwohl meine Landsleute es gerne sehen würden, wenn Ihr eine aktive Rolle in den Geschehnissen in Zul\'Drak übernehmen würdet, sind wir Zandalari doch in erster Linie hier, um zu bezeugen, was geschieh', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13287', 'deDE', 'Seid Ihr bereit dazu, zusammen mit mir Mal\'Ganis gegenüberzutreten?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13288', 'deDE', 'Alles wurde vorbereitet und ist startbereit.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13289', 'deDE', '', 'Geht weiter. Hier gibt es nichts zu sehen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13290', 'deDE', '', '<Datura beäugt Euch neugierig.>$B$BIch kann nicht anders, als mich zu fragen, warum Ihr all dieser Aufmerksamkeit würdig seid.$B$BNichts für ungut...', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13291', 'deDE', 'Die Drakkari halten meinen Körper im Altargebäude an der Schwelle des Todes. Sie können meinen Geist jedoch nicht sehen.$B$BWir müssen zusammenarbeiten, um zu verhindern, dass sie an meine Kräfte gela', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13293', 'deDE', 'Bitte beeilt Euch, $N. Ich habe große Schmerzen und die Zeit läuft mir davon.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13300', 'deDE', 'Ohh... gugge ma\' der süse gleine Ghuul.$B$BSchbring, gleiner Ghuul! Schbring!$B$BHa, ha, ha... So\'n guda, gleiner Ghuul.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13303', 'deDE', '', 'Die Geißel ist ein Schandfleck in diesem makellosen Land. Bei der Macht der Schöpfer, ich werde jeden einzelnen ihrer klapprigen Knochen unter meiner Ferse zerquetschen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13304', 'deDE', '', 'Die Titanen haben Leben nicht über Nacht erschaffen. Dieser Prozess ist graduell und bedarf erheblichen Experimentierens und vieler Versuche.$B$BDeshalb gibt es solch schwer geschützte Gegenden wie da', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13305', 'deDE', '', 'Es grenzt an Frevel, dass diese Geheimnisse einem Sterblichen wie Euch offenbart werden, jedoch habe ich keine Wahl.$B$BSeitdem mich Freya, die Tochter der Titanen, hier zurückgelassen hat, hatte ich ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13306', 'deDE', 'Dieser Außenposten ist zahnradfreie Zone. Leute, die beim Bau von mechanischen Geräten Zahnräder verwenden, werden erbarmungslos verspottet.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13307', 'deDE', 'Sieht so aus, als hättet Ihr dieselbe Idee gehabt wie ich. Ihr jagt den Früchten nach, oder?', 'Sieht so aus, als hättet Ihr dieselbe Idee gehabt wie ich. Ihr jagt den Früchten nach, oder?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13308', 'deDE', 'Loken?! Das ist doch einfach lästig... Wir haben vielleicht die Eisenzwerge ausgeschaltet, doch ich möchte wetten, dass es irgendwo noch eine weitere Maschine gibt, die am laufenden Band massenweise E', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13310', 'deDE', 'Außerhalb von Orgrimmar gibt es zwei Zeppelintürme. Der östliche Turm ist die Verbindung nach Tirisfal und zum Schlingendorntal, während der westliche Turm die Verbindung nach Nordend und Donnerfels i', 'Außerhalb von Orgrimmar gibt es zwei Zeppelintürme. Der östliche Turm ist die Verbindung nach Tirisfal und zum Schlingendorntal, während der westliche Turm die Verbindung nach Nordend und Donnerfels i', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13311', 'deDE', 'Verlasst die Stadt und sucht im Südosten nach dem östlichen Zeppelinlandeturm.', 'Verlasst die Stadt und sucht im Südosten nach dem östlichen Zeppelinlandeturm.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13318', 'deDE', 'Der Mooswandler sieht schwer verwundet aus.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13321', 'deDE', '', 'Von diesem Dock aus fährt die Bravado zwischen Sturmwind und Auberdine hin und her.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13322', 'deDE', 'Meine arme Flugmaschine. Sie hat den Absturz nicht überlebt und ohne Ersatzteile kriege ich sie nie wieder zum Laufen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13324', 'deDE', 'Schon bald, $N!$B$BSchon bald werdet Ihr den Geschmack wahrer Macht kennen lernen. Schon bald werdet Ihr für Euren Einsatz und Eure Dienste belohnt werden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13325', 'deDE', 'Ihr seid auf dem Pfad, Sterblicher...$B$BHaltet aus und Ihr erhaltet große Macht!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13326', 'deDE', 'Mphmm rmphhimm rrhumghph?', '', 'Rmmurphum mmph hmrphum!', '', 'Umphrum mmphur, murruphru...', '', 'Mhrrumph rummrhum phurr!', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13328', 'deDE', 'Willkommen, darf ich Euch etwas anbieten?', 'Willkommen, darf ich Euch etwas anbieten?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13331', 'deDE', '', 'Gefällt Euch das neue Heim, das ich aus meinen sogenannten Anhängern gefertigt habe?$B$BWährend meines Todeskampfs habe ich sie alle verflucht und mit mir in diese besondere Unterwelt meiner eigenen S', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13342', 'deDE', 'Seid Ihr gekommen, um von Hargus etwas zu kaufen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13348', 'deDE', 'Noch\'n Ersucher der Gunst vom Lichkönig?$B$BNur Würdige erhalten die dunklen Gaben.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13349', 'deDE', 'Willkommen bei der Gildenregistrierung von Dalaran. Wie kann ich Euch helfen?', 'Willkommen bei der Gildenregistrierung von Dalaran. Wie kann ich Euch helfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13352', 'deDE', 'Ich hoffe, der Erzmagier weiß zu schätzen, was ich durchmache, um Hemets Teil der Vereinbarung zu erfüllen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13353', 'deDE', '', 'Ihr solltet im Moment in Arthas\' Nähe bleiben!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13355', 'deDE', 'Wir werden ihnen alles nehmen, Todesritter.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13359', 'deDE', 'Wildherzen nehmen Land. Großzungen gehen oder wir töten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13360', 'deDE', 'Wildherzen können böse werden, aber wir gute Leute. Du sehen, wenn du bleiben bisschen. Wir gute Freunde mit Klug und Stark.$B$BWir nur nix mögen Dumm.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13361', 'deDE', 'Hier Großes Regenstein. Wir beschützen und ihm geben Glitzer-Glitzer und er sich um uns kümmern.$B$BDu mehr Glitzer-Glitzer für Stein vielleicht?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13362', 'deDE', 'Wo ich nix Größe, ich stark Magie. Ich mich kümmern um Großes Nebelstein gut... meistens jedenfalls.$B$BBlöde Wauwauleute.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13363', 'deDE', 'Wauwauleute uns angreifen seit Tagen. Großes Blitzstein noch immer sicher aber.$B$BSoo-holu kommen uns helfen, wie schon oft... Wauwauleute keine Chance.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13364', 'deDE', 'Kartak kommt für Großzungenfleisch. Wildherzen sicherstellen, dass viel bereit.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13365', 'deDE', 'Hallo Trockenhaut.', '', 'Was glotzt\'n so?', '', 'Gibt es einen Grund dafür, dass wir noch immer hier rumstehen?', '', 'Ich nehme an, Ihr wollt etwas.', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13366', 'deDE', 'Alles okay?', '', 'Jup?', '', 'Es ist schön, eine gute Jagdgruppe zu haben.', '', 'Stimmt was nicht?', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13367', 'deDE', 'Hallo! Wie geht\'s? Uns gut... jup, gut, gut. Hallo!', '', 'Schon mal über die eigenen Füße gestolpert? Wir schon. Tut irgendwie weh. Nicht machen, nicht gut. Nein.', '', 'Hallo! Ihr seid ziemlich stark und nett und so. Ich mag Euch. Lasst uns Freunde bleiben, okay?', '', 'Wollt Ihr, dass wir bei irgendwas helfen? Bei was wir helfen? Wir sind gute Helfer.', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13368', 'deDE', '', 'Ich stehe für immer in Eurer Schuld, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13375', 'deDE', 'Ein schöner Tag zum Fliegen, oder?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13376', 'deDE', 'Ich erkenne ein Ass, wenn ich eins sehe. Danke nochmals, dass Ihr mein Leben gerettet habt!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13382', 'deDE', 'Schaut, was Ihr angerichtet habt, Todesritter! Ihr habt der Geißel Ruhm gebracht!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13390', 'deDE', 'Lass mich hier raus, Mann!', '', 'Du bist nich\' von der Geißel. Was machste hier, Mann?', '', 'Warum lässt die Geißel dich hier herumlaufen, Mann?', '', 'Ich glaub\', du bist verloren, Mann...', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13391', 'deDE', 'Die Geißel is\' in Zul\'Drak nicht willkommen!', '', 'Zul\'Drak gehört den Drakkari, Mann. Das wird immer so sein!', '', 'Deine Geißel wird hier versagen, Mann.', '', 'Unsre Leute werd\'n Euch Geißler bis auf den Letzten umhau\'n!', '', 'Haut ab, garstige Geißel!', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13395', 'deDE', 'Im Moment mag es noch wie ein gewöhnliches Kürbisbeet aussehen, aber sobald der Seuchenkessel platziert ist, wird daraus eine Quelle von Krankheit, Fäule und Seuche!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13396', 'deDE', 'Wie könnt Ihr es wagen? Geht mir aus den Augen!', '', 'Dieser nichtsnutzige Schleimer trainiert wahrscheinlich Ballett in seinen Gemächern.', '', 'Sprecht diesen Namen NIEMALS in meiner Nähe aus! Betrachtet dies als die erste und einzige Warnung!', '', 'Ich kann Euch sagen, wo er nicht ist...', '', 'Heigan führt wahrscheinlich Wartungsarbeiten an seinem mit Schleim und Dreck gefüllten Tanzstudio aus.', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13397', 'deDE', 'Es ist nur eine Frage der Zeit, bis die Seuche Neu-Avalon übernimmt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13402', 'deDE', 'Fineous hat, was Ihr braucht, $G mein Freund:meine Freundin;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13419', 'deDE', 'Kreuzfahrer...$B$B<Totenschwur schüttelt den Kopf.>$B$BSo etwas wie sie habe ich unter den Menschen noch nie gesehen. Meine Theorie ist, dass sie über keine höhere Gehirnfunktion verfügen und dadurch ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13422', 'deDE', '$N! Ich habe auf deine Rückkehr gewartet. Was hat dich so lang\' aufgehalten, Mann?$B$BEs is\' Zeit, die Konvertierung des Seuchenbluts zu beenden.$B$BSchließ dich mir an und werde Zeuge der Erschaffung', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13425', 'deDE', 'Ich bin schwer verletzt, doch ich tue, was ich kann, um zu helfen. Versucht, innerhalb meiner antimagischen Barriere zu bleiben oder die Magie der Akolyten wird Euch in Stücke reißen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13426', 'deDE', 'Was kann ich für Euch tun, $R?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13430', 'deDE', '<Koltira stöhnt.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13431', 'deDE', '', 'Es ist nicht ganz das, wofür ich mich gemeldet hatte, aber, hey,… wenigstens ist es hier warm.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13432', 'deDE', 'Der Scharlachrote Kreuzzug wird einem neuen Herren dienen - im Tod...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
