INSERT INTO `npc_text_localized` VALUES ('13441', 'deDE', 'Seid gegrüßt, $C. Bringt Ihr mir gute Nachrichten vom Worldwide Invitational Event? Falls dies der Fall ist, habt Ihr sicherlich einen geheimen Code erhalten. Wenn Ihr mir diesen Code verratet, werde ', 'Seid gegrüßt, $C. Bringt Ihr mir gute Nachrichten vom Worldwide Invitational Event? Falls dies der Fall ist, habt Ihr sicherlich einen geheimen Code erhalten. Wenn Ihr mir diesen Code verratet, werde ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13444', 'deDE', 'Wir sind die Auserwählten, $N. Todesritter der Geißel, entsendet, um die Samen der Zerstörung des Herren zu säen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13445', 'deDE', '', 'Diese Monster haben meinen Vater getötet und meine Schwester entführt. Dafür werden sie bezahlen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13449', 'deDE', '', 'Willkommen in meiner Küche! Hier bereiten wir ein paar der feinsten Speisen zu, die es in Dalaran gibt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13451', 'deDE', 'Hoffe, Ihr es den Großzungen richtig zeigen. Es wird Zeit, dass sie weggeh\'n.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13452', 'deDE', 'Großartige Schlacht im Gange bei der Zuflucht der Funkenbesprühten.$B$BVielleicht könnt Ihr zwischendurch ein paar Minuten entbehren, um ein wenig auszuhelfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
