INSERT INTO `npc_text_localized` VALUES ('13456', 'deDE', '', 'Als Magier seid Ihr Mitglied einer erlesenen Gruppe, die ein Portal herbeizaubern kann, das Euch wieder an genau diesen Platz zurückbringen kann. Ich kann es Euch beibringen, wenn Ihr bereit seid.$B$B', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
