INSERT INTO `npc_text_localized` VALUES ('14053', 'deDE', 'Woah, woah, woah! Seid leise, $N. Seid leise!$B$B<Bumm schaut sich um.>$B$BWir wollen doch nicht, dass diese eiskalten Vrykul uns bemerken, oder?$B$BWas haltet Ihr von meinem Hüpfboter? Ich hab ihn mi', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
