INSERT INTO `npc_text_localized` VALUES ('14064', 'deDE', 'Leben hängen von den Entscheidungen ab, die wir hier treffen. Sagt schnell, was Ihr zu sagen habt, $C.', '', 'Bringt Ihr Neuigkeiten von der Schlacht, $R?', '', 'Der beste Krieg ist ein schnell beendeter. Dasselbe lässt sich von einer Konversation behaupten. Was meint Ihr, $R?', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14065', 'deDE', '', 'Dieses Portal führt zu den Höhlen der Zeit, tief unter der Erde im fernen Tanaris.$B$BIch verfüge über die Macht, Euch dorthin zu schicken, wenn Ihr dies wünscht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
