INSERT INTO `npc_text_localized` VALUES ('14120', 'deDE', 'Ich gehörte zu den ersten Taurendruiden und zur letzten Generation, die Shan\'do Cenarius unterrichtet hatte - damals, vor der Legion, vor der Teilung, als die Welt noch jung war - bevor sich alles änd', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
