INSERT INTO `npc_text_localized` VALUES ('14131', 'deDE', '', 'Einen weiteren Krieg mit der Horde müssen wir um jeden Preis vermeiden!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
