INSERT INTO `npc_text_localized` VALUES ('14137', 'deDE', '', 'Wir haben bereits einige gute Leute hier oben verloren. Ich beginne daran zu zweifeln, ob dieser Ort unsere Aufmerksamkeit überhaupt wert ist.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14138', 'deDE', '', 'Seid gegrüßt, $R. Eure Anwesenheit ehrt mich.$B$BWie kann ich Euch helfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14140', 'deDE', 'Bereitet Euch vor, $N. Wir greifen an, sobald all Eure Truppen eingetroffen sind!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14142', 'deDE', 'Die letzte Schlacht wird bald beginnen, $N. Still gestanden!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14150', 'deDE', 'Ah, Ihr seid\'s, $N! Willkommen in unserem Heiligtum. Erholt und erfrischt Euch; Ihr habt es Euch mehr als verdient.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14151', 'deDE', '<Die Leibwache der Königin nickt in Anerkennung Eurer Anwesenheit.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14155', 'deDE', '', 'War ein geschäftiger Tag in der Luft, was, $N? Vielleicht solltet Ihr einen Moment Luft holen, bevor Ihr wieder startet.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14159', 'deDE', 'Vorsicht, $N. Wo ist Eure Kultistenkapuze?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14160', 'deDE', 'Da seid Ihr ja. Seid Ihr bereit? Wenn wir einmal angefangen haben, wird es kein Zurück mehr geben.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
