INSERT INTO `npc_text_localized` VALUES ('14162', 'deDE', 'Willkommen im Zauberkasten, $C. Ich hoffe, Ihr werdet Euren Aufenthalt hier genießen.', 'Willkommen im Zauberkasten, $C. Ich hoffe, Ihr werdet Euren Aufenthalt hier genießen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14163', 'deDE', 'Willkommen in der Taverne \'Zur Zauberkrähe\', $R.$B$BEin Wort der Warnung: Seht Euch hier besser vor. Narisa mag es nicht besonders, Blut vom Boden wischen zu müssen.', 'Willkommen in der Taverne \'Zur Zauberkrähe\', $R.$B$BEin Wort der Warnung: Seht Euch hier besser vor. Narisa mag es nicht besonders, Blut vom Boden wischen zu müssen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14165', 'deDE', 'Solltet Ihr abgeschossen und gefangen werden, dann nennt nicht Euren richtigen Namen. Benutzt einen Codenamen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14168', 'deDE', '', 'Die meisten Drachen hätten Angst davor, sich hier zu bewegen, $R.$B$BWas kann einen Sterblichen nur dazu bewegt haben, an einen solch tückischen Ort zu kommen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14169', 'deDE', '', 'Habt Ihr den Mut, Euch den Elitedrachen von Malygos in ihrem eigenen Reich zu stellen, $R?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14172', 'deDE', 'Entschuldigt, $GFreund:Freundin;. Nur ausgewiesene Offiziere der Allianz können den Kauf eines Fahrzeugs autorisieren.', 'Entschuldigt, $GFreund:Freundin;. Nur ausgewiesene Offiziere der Allianz können den Kauf eines Fahrzeugs autorisieren.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14174', 'deDE', 'Diese uralten Folianten können immer in der Nähe von Inschriftenkundelehrern gefunden werden.', 'Diese uralten Folianten können immer in der Nähe von Inschriftenkundelehrern gefunden werden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14176', 'deDE', 'Wir haben es geschafft! Ist es zu glauben, was wir hier hören? Es ist kaum zu fassen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14177', 'deDE', 'Ist es nicht phantastisch? Könnt Ihr den möglichen Profit sehen?', '', 'Khit\'rix der dunkle Meister jagt von Südosten nach Valhalas herein!', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14178', 'deDE', 'Ein wirklich schöner Tag, findet Ihr nicht? Ich hoffe, wir sehen noch ein wenig Feuerwerk, bevor der Tag sich dem Ende neigt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
