INSERT INTO `npc_text_localized` VALUES ('14180', 'deDE', '', 'Nur weil etwas nicht kaputt ist, heißt das ja nicht, dass man es nicht reparieren könnte. Sucht Ihr was Bestimmtes, Liebelein?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14182', 'deDE', 'Die I.D.S.V.-Ausgabe poltert erwartungsvoll.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14192', 'deDE', 'Willkommen in meinem bescheidenen Heim, $C. Ich empfinde diesen Ort als sehr entspannend und fand es immer großartig, in Dalaran zu Hause zu sein. Manchmal ist es allerdings schon ein wenig einsam hie', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14194', 'deDE', 'Verblüffend! Hier sind Passagen, die ich zuvor noch nie gesehen habe. Das Ausfindigmachen muss Euch einiges an Zeit gekostet haben. Solltet Ihr noch mehr solcher Bücher finden, so wäre ich glücklich, ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14197', 'deDE', 'Eine zünftige Schatzjagd und ein wenig herzhaftes Bier - besser geht\'s nicht, $G Kumpel:meine Liebe;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14198', 'deDE', 'Der Smaragdgrüne Traum ist zu einem gefährlichen Ort geworden. Ich mache mir mehr und mehr Sorgen um diejenigen, die ihr Leben im Wachzustand in Azeroth opfern, in der Hoffnung, es zu retten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14203', 'deDE', 'Die Hämmer der Irdenen stehen geschlossen hinter Bergfels. Wir werden uns Lokens Günstlingen bis zum Ende entgegenwerfen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
