INSERT INTO `npc_text_localized` VALUES ('14241', 'deDE', 'Seid gegrüßt, $C.$B$BWenn die Horde die Tausendwinterfestung kontrolliert, werde ich ein Portal öffnen für diejenigen, die dorthin reisen möchten. $B$BDie Schlacht läuft gerade. Macht Euch auf nach Ta', 'Seid gegrüßt, $C.$B$BWenn die Horde die Tausendwinterfestung kontrolliert, werde ich ein Portal öffnen für diejenigen, die dorthin reisen möchten. $B$BDie Schlacht läuft gerade. Macht Euch auf nach Ta', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
