INSERT INTO `npc_text_localized` VALUES ('14248', 'deDE', '', 'Grüße, $C! Willkommen an Bord der Wolkenkuss. Nordend kann ein sehr rauer Ort sein. Ich hoffe, Ihr habt Euch vorbereitet.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
