INSERT INTO `npc_text_localized` VALUES ('14272', 'deDE', 'Ich weiß, ich weiß... \"in der Kanalisation angeln?\" Aber Ihr wärt überrascht, was man hier unten alles rausholen kann. Ich habe schon Geschichten von Haien gehört, ob Ihr\'s glaubt oder nicht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
