INSERT INTO `npc_text_localized` VALUES ('14384', 'deDE', 'Seid Ihr sicher, dass Ihr bereit seid, Euch einem Recken zu stellen?', 'Seid Ihr sicher, dass Ihr bereit seid, Euch einem Recken zu stellen?', 'Es wäre unklug, davon auszugehen, dass der Kampf gegen einen Recken eine einfache Aufgabe wäre. Seid Ihr sicher, dass Ihr bereit seid?', 'Es wäre unklug, davon auszugehen, dass der Kampf gegen einen Recken eine einfache Aufgabe wäre. Seid Ihr sicher, dass Ihr bereit seid?', 'Seid Ihr sicher, dass Ihr bereit seid, gegen mich anzutreten?', 'Seid Ihr sicher, dass Ihr bereit seid, gegen mich anzutreten?', '', '', '', '', '', '', '', '', '', '');
