INSERT INTO `npc_text_localized` VALUES ('14395', 'deDE', 'Wir sind hierhergekommen, um Ehre, Ansehen und das Recht, an der Seite der Brüder des Argentumkreuzzugs gegen die Geißel anzutreten, zu streiten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14396', 'deDE', '', 'König Magni erwartet von uns, dass wir dieses Turnier gewinnen und ihn stolz machen. Wir dürfen nicht scheitern.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
