INSERT INTO `npc_text_localized` VALUES ('14408', 'deDE', 'Wenn Ihr bereit seid, Euch dem Test zum Range des Recken zu unterziehen, so werde ich ein Mitglied des Argentumkreuzzugs herbeirufen.', 'Wenn Ihr bereit seid, Euch dem Test zum Range des Recken zu unterziehen, so werde ich ein Mitglied des Argentumkreuzzugs herbeirufen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14409', 'deDE', 'Dies ist der Ring, in dem die Helden den Kampf gegeneinander üben. Es ist meine Aufgabe, die Ritter zu ihren Übungskämpfen herbeizurufen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
