INSERT INTO `npc_text_localized` VALUES ('14442', 'deDE', 'Das habt Ihr tatsächlich, Mann. Unser Vorrat an Seuchenkristallen ist fast ausreichend.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
