INSERT INTO `npc_text_localized` VALUES ('14453', 'deDE', 'Ich kann Euch nicht unterrichten, solange Ihr nicht auf einem Turnierreittier sitzt.$B$BNehmt Eure Lanze und haltet in der Nähe des Pavillons Eurer Fraktion nach Eurem Turnierreittier Ausschau.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
