INSERT INTO `npc_text_localized` VALUES ('14496', 'deDE', 'Die Aktivierung des sekundären Verteidigungssystems wird zum Auslöschen unautorisierter Lebensformen mittels orbitaler Stellungen führen.$B$BIhr seid eine unautorisierte Lebensform. Bitte bestätigen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
