INSERT INTO `npc_text_localized` VALUES ('14678', 'deDE', 'Gut gemacht! Eure Kämpfe gegen all diese Bestien waren einfach fabelhaft! Wie ich höre wird die nächste Herausforderung noch härter!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
