INSERT INTO `npc_text_localized` VALUES ('14712', 'deDE', '', 'Willkommen in unserem Laden. Wir haben alles, was Ihr braucht... solange es Alchemiezubehör ist.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14713', 'deDE', '', 'Nein, ich bin hier die Intellektuelle und man sollte mich nicht auf die leichte Schulter nehmen. Ich könnte einen Heiltrank aus nichts anderem als Abwaschwasser und Pusteblumen herstellen, allerdings ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14723', 'deDE', 'Die Geister der Elemente sind weise. Sie waren lange vor uns hier und sie werden fortbestehen, lange nachdem wir verschwunden sind. Wenn sie zu uns sprechen, müssen wir zuhören.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14736', 'deDE', 'Es gibt diejenigen, die keine Erfahrung aus ihren Handlungen ziehen möchten, die für immer auf der Stufe an Stärke verbleiben möchten, die sie jetzt haben, und die niemals stärker werden wollen.$B$BIc', 'Es gibt diejenigen, die keine Erfahrung aus ihren Handlungen ziehen möchten, die für immer auf der Stufe an Stärke verbleiben möchten, die sie jetzt haben, und die niemals stärker werden wollen.$B$BIc', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
