INSERT INTO `npc_text_localized` VALUES ('14738', 'deDE', 'Ihr habt alle hier mit Euren Fähigkeiten in der Arena beeindruckt. Jetzt ist es an der Zeit, vor allen als Champion des Turniers anerkannt zu werden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
