INSERT INTO `npc_text_localized` VALUES ('14777', 'deDE', 'Seid gegrüßt, $C.$B$BDerzeit kontrolliert die Horde die Tausendwinterfestung, doch die Schlacht hat begonnen! Macht Euch auf nach Tausendwinter und unterstützt unsere Truppen!', 'Seid gegrüßt, $C.$B$BDerzeit kontrolliert die Horde die Tausendwinterfestung, doch die Schlacht hat begonnen! Macht Euch auf nach Tausendwinter und unterstützt unsere Truppen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14779', 'deDE', 'Wir haben uns schon seit einiger Zeit um diese Würmer gekümmert. Ich hoffe, dass sie rechtzeitig fit sein werden, um an der Ringveranstaltung dieser Organisation teilzunehmen. Ihr müsst Euch immerhin ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14781', 'deDE', 'Seid gegrüßt, $C.$B$BDerzeit kontrolliert die Allianz die Tausendwinterfestung, doch die Schlacht hat begonnen! Macht Euch auf nach Tausendwinter und unterstützt unsere Truppen!', 'Seid gegrüßt, $C.$B$BDerzeit kontrolliert die Allianz die Tausendwinterfestung, doch die Schlacht hat begonnen! Macht Euch auf nach Tausendwinter und unterstützt unsere Truppen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14782', 'deDE', 'Seid gegrüßt, $C.$B$BDerzeit kontrolliert die Allianz die Tausendwinterfestung. Wenn die Schlacht unmittelbar bevorsteht, könnt Ihr mich ansprechen, um dem Kampf beizutreten.$B$BDie nächste Schlacht b', 'Seid gegrüßt, $C.$B$BDerzeit kontrolliert die Allianz die Tausendwinterfestung. Wenn die Schlacht unmittelbar bevorsteht, könnt Ihr mich ansprechen, um dem Kampf beizutreten.$B$BDie nächste Schlacht b', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14783', 'deDE', 'Guten Tag, $Gmein Herr:werte Dame;! Wie kann ich Euch behilflich sein?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14785', 'deDE', 'Dieser Riss im Gewebe von Zeit und Raum sieht bedrohlich aus. Verschiedene Szenen rasen in schreckenerregender Geschwindigkeit vorbei. Eine sehr mutige Person würde es vielleicht wagen, zum richtigen ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14787', 'deDE', 'Ihr! Ja, Ihr!$B$BSeht mich an und behaltet diesen Augenblick in Erinnerung, denn schon bald wird die Welt den Namen und das strenge Gesicht Wilfried Zischknalls kennen und fürchten lernen! Sobald mein', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14790', 'deDE', 'Seid gegrüßt, $C.$B$BDerzeit kontrolliert die Horde die Tausendwinterfestung, aber die nächste Schlacht steht unmittelbar bevor! Macht Euch bereit, nach Tausendwinter aufzubrechen und unsere Truppen z', 'Seid gegrüßt, $C.$B$BDerzeit kontrolliert die Horde die Tausendwinterfestung, aber die nächste Schlacht steht unmittelbar bevor! Macht Euch bereit, nach Tausendwinter aufzubrechen und unsere Truppen z', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14791', 'deDE', 'Seid gegrüßt, $C.$B$BDerzeit kontrolliert die Allianz die Tausendwinterfestung, aber die nächste Schlacht steht unmittelbar bevor! Macht Euch bereit, nach Tausendwinter aufzubrechen und unsere Truppen', 'Seid gegrüßt, $C.$B$BDerzeit kontrolliert die Allianz die Tausendwinterfestung, aber die nächste Schlacht steht unmittelbar bevor! Macht Euch bereit, nach Tausendwinter aufzubrechen und unsere Truppen', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14793', 'deDE', '', 'Justiziarin Mariel Treuherz hat uns darum gebeten, uns um alles rund ums Turnier zu kümmern. Diejenigen, die sich das Recht verdient haben, als Champions anzutreten und die sich in unseren Augen bewie', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14794', 'deDE', '', 'Justiziarin Mariel Treuherz hat uns gebeten, bei der Organisation und der Verteidigung des Turniers zu helfen, jetzt, da das Kolosseum fertig gestellt ist. Sie will, dass wir mit diesen wertlosen Hund', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14804', 'deDE', 'Es mag sein, dass wir im Schatten höflicher Gesellschaft operieren, doch hier draußen, auf dem Schlachtfeld, erfahren unsere Kräfte endlich den gebührenden Respekt.', 'Es mag sein, dass wir im Schatten höflicher Gesellschaft operieren, doch hier draußen, auf dem Schlachtfeld, erfahren unsere Kräfte endlich den gebührenden Respekt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14813', 'deDE', 'Der Hexenmeister hat ganz sicher keine Verdammniswache beschworen! Eure nächste Herausforderung besteht darin, Euch den Rittern des Argentumkreuzzugs zu stellen. Glaubt Ihr, dass Ihr dafür bereit seid', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14814', 'deDE', 'Nun gut, ich werde Tirion ein Signal geben, wenn Ihr bereit seid, anzufangen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14815', 'deDE', 'Der Kampf kann hart und brutal werden. Einen Arenakampf wie diesen habe ich seit Jahren nicht mehr gesehen! Faszinierend!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14816', 'deDE', 'Ich kann es nicht abwarten, das zu sehen! Sie scheinen für eine weitere Runde bereit zu sein - seid Ihr es auch?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14819', 'deDE', 'Viele sind gestorben, damit die nächste Herausforderung für Euch bereitstehen kann!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14820', 'deDE', 'Die Zusammenarbeit der Val\'kyr ist wirklich erstaunlich. Jetzt verstehe ich auch, warum wir solche Probleme hatten, die beiden einzufangen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14821', 'deDE', 'Da könnt Ihr Gift drauf nehmen! Ihr werdet zwei von Arthas\' mächtigsten Kommandanten, den Val\'kyr, gegenüberstehen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14822', 'deDE', 'Nun gut, ich hoffe, Ihr verbessert Euch schnell!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14824', 'deDE', '', 'Die Seneschallin des Turniers hat uns gebeten, bei der Organisation des Wettbewerbs mitzuhelfen, nun da das Kolosseum fertig gestellt ist. Diejenigen, die sich das Recht verdient haben, sich Champions', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14828', 'deDE', 'Großartig. Ihr habt Euch heute als große Champions bewiesen. Mit Euch an der Seite des Argentumkreuzzugs wird der Sieg gegen die Geißel unausweichlich sein!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14829', 'deDE', 'Das Lob ist wohlverdient. Tirion wird Euch schon bald selbst gratulieren wollen. Hey, habt Ihr auch bemerkt, dass es plötzlich ein wenig kalt geworden ist?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14850', 'deDE', 'Willkommen, Champion! Habt Ihr Eure Truppen versammelt und Euch auf die Herausforderungen der Prüfungen des Obersten Kreuzfahrers vorbereitet? Nur den mächtigsten und versiertesten Kämpfern wird es mö', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14853', 'deDE', 'Die Prüfung des Obersten Kreuzfahrers ist um einiges schwieriger zu meistern als die Prüfung des Kreuzfahrers. Jeder Kampf ist schwieriger und Ihr habt nicht die Möglichkeit, Euch so lange ins Kampfge', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14865', 'deDE', 'Nun, da Ihr die Prüfung des Kreuzfahrers abgeschlossen habt, solltet Ihr Euch überlegen, wiederzukommen, um an der Prüfung des Obersten Kreuzfahrers teilzunehmen. Nur die mächtigsten Abenteurer dürfen', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14866', 'deDE', 'Herzlichen Glückwunsch! Bitte, benutzt das Portal nach Dalaran, wenn Ihr so weit seid.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14882', 'deDE', '', 'Es ist schön, Euch wiederzusehen, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14899', 'deDE', 'Ihr findet sie im Apothekarium. Die Namen lauten Doktor Herbert Halsey und Doktor Marsch. Aber seid bloß vorsichtig!', 'Ihr findet sie im Apothekarium. Die Namen lauten Doktor Herbert Halsey und Doktor Marsch. Aber seid bloß vorsichtig!', 'Doktor Herbert Halsey ist derjenige, den Ihr sucht. Aber kommt nicht auf dumme Ideen! Wenn Ihr mit dem Seuchenunsinn nicht aufhört, könnt Ihr ganz schnell herausfinden, wie unbequem eine Axt sein kann', 'Doktor Herbert Halsey ist derjenige, den Ihr sucht. Aber kommt nicht auf dumme Ideen! Wenn Ihr mit dem Seuchenunsinn nicht aufhört, könnt Ihr ganz schnell herausfinden, wie unbequem eine Axt sein kann', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14900', 'deDE', 'Begebt Euch zum inneren Ring; dort findet Ihr Auktionatoren.', 'Begebt Euch zum inneren Ring; dort findet Ihr Auktionatoren.', 'Wenn Ihr am Marktgeschehen hier teilhaben wollt, dann begebt Euch zum inneren Ring der Stadt.', 'Wenn Ihr am Marktgeschehen hier teilhaben wollt, dann begebt Euch zum inneren Ring der Stadt.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14901', 'deDE', 'Die Bank? Das ist der Turm in der Mitte des Handwerksviertels.', 'Die Bank? Das ist der Turm in der Mitte des Handwerksviertels.', 'Sie befindet sich im Zentrum der Stadt. Die Radnarbe.', 'Sie befindet sich im Zentrum der Stadt. Die Radnarbe.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14902', 'deDE', 'Naznik ist der Barbier hier im Ort. Er hat sein Geschäft auf dem obersten Ring des Handwerksviertels.', 'Naznik ist der Barbier hier im Ort. Er hat sein Geschäft auf dem obersten Ring des Handwerksviertels.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14903', 'deDE', 'Michael Garrett kümmert sich um die Fledermäuse Unterstadts. Er hält sich auf dem oberen Ring im Handwerksviertel auf.', 'Michael Garrett kümmert sich um die Fledermäuse Unterstadts. Er hält sich auf dem oberen Ring im Handwerksviertel auf.', 'Der Fledermausführer hat seinen Schlafplatz auf dem oberen Ring des Handwerksviertels. Ihr könnt den Geruch gar nicht verfehlen.', 'Der Fledermausführer hat seinen Schlafplatz auf dem oberen Ring des Handwerksviertels. Ihr könnt den Geruch gar nicht verfehlen.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14904', 'deDE', 'Die Kampfmeister sind im Königsviertel stationiert; Ihr findet sie im Thronsaal.', 'Die Kampfmeister sind im Königsviertel stationiert; Ihr findet sie im Thronsaal.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14905', 'deDE', 'James Van Brunt betreibt im Kriegsviertel eine ganz annehmbare Schmiede. Sie befindet sich am inneren Ring.', 'James Van Brunt betreibt im Kriegsviertel eine ganz annehmbare Schmiede. Sie befindet sich am inneren Ring.', 'Der hiesige Schmied arbeitet im Kriegsviertel, wo seine Waffen oft genutzt werden. Dort befindet er sich am inneren Ring.', 'Der hiesige Schmied arbeitet im Kriegsviertel, wo seine Waffen oft genutzt werden. Dort befindet er sich am inneren Ring.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14906', 'deDE', 'Was hier als Essen durchgeht, wird drüben im Handwerksviertel von Eunice Burch gekocht. Es ist Euer Magen, nicht meiner.', 'Was hier als Essen durchgeht, wird drüben im Handwerksviertel von Eunice Burch gekocht. Es ist Euer Magen, nicht meiner.', 'Die hiesige Köchin befindet sich im Handwerksviertel. Ihr Name ist Eunice Burch.', 'Die hiesige Köchin befindet sich im Handwerksviertel. Ihr Name ist Eunice Burch.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14907', 'deDE', 'Lavinia Raab ist die ansässige Verzauberin. Ihren Laden findet ihr am inneren Ring des Apothekariums.', 'Lavinia Raab ist die ansässige Verzauberin. Ihren Laden findet ihr am inneren Ring des Apothekariums.', 'Wenn Ihr etwas über die Verzauberkunst lernen wollt, geht zu Lavinia Raab am inneren Ring des Apothekariums.', 'Wenn Ihr etwas über die Verzauberkunst lernen wollt, geht zu Lavinia Raab am inneren Ring des Apothekariums.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14908', 'deDE', 'Der Ingenieur hier heißt Franklin Lloyd. Er schraubt im Schurkenviertel herum, am äußeren Ring.', 'Der Ingenieur hier heißt Franklin Lloyd. Er schraubt im Schurkenviertel herum, am äußeren Ring.', 'Schaut, wenn Ihr unbedingt mit diesen Ingenieuren sprechen wollt, geht zum äußeren Ring des Schurkenviertels. Ist Eure Haut.', 'Schaut, wenn Ihr unbedingt mit diesen Ingenieuren sprechen wollt, geht zum äußeren Ring des Schurkenviertels. Ist Eure Haut.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14909', 'deDE', 'Ihr sucht nach Mary Edras. Ihr könnt sie auf dem inneren Ring des Schurkenviertels finden.', 'Ihr sucht nach Mary Edras. Ihr könnt sie auf dem inneren Ring des Schurkenviertels finden.', 'Sogar die wandelnden Toten müssen ihre Wunden versorgen. Mary Edras kümmert sich darum. Sie ist im Schurkenviertel, am inneren Ring.', 'Sogar die wandelnden Toten müssen ihre Wunden versorgen. Mary Edras kümmert sich darum. Sie ist im Schurkenviertel, am inneren Ring.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14910', 'deDE', 'Der verrückte Angler ist an der Anlegestelle im Magieviertel.', 'Der verrückte Angler ist an der Anlegestelle im Magieviertel.', 'Ich weiß zwar nicht, was er dort fangen will, aber Armand Cromwell ist an der Anlegestelle im Magieviertel.', 'Ich weiß zwar nicht, was er dort fangen will, aber Armand Cromwell ist an der Anlegestelle im Magieviertel.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14911', 'deDE', 'Der Gildenmeister ist Christopher Drakul. Er befindet sich auf der östlichen Plattform des Handwerksviertels.', 'Der Gildenmeister ist Christopher Drakul. Er befindet sich auf der östlichen Plattform des Handwerksviertels.', 'Der Gildenmeister betreibt sein Geschäft im Handwerksviertel. Er ist oben auf der östlichen Plattform.', 'Der Gildenmeister betreibt sein Geschäft im Handwerksviertel. Er ist oben auf der östlichen Plattform.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14912', 'deDE', 'Die hiesige Kräuterkundige ist Martha Alliestar. Sie ist drüben am inneren Ring des Apothekariums.', 'Die hiesige Kräuterkundige ist Martha Alliestar. Sie ist drüben am inneren Ring des Apothekariums.', 'Begebt Euch zum inneren Ring des Apothekariums. Martha Alliestar ist dort, sie wird Euch in der Kräuterkunde unterrichten.', 'Begebt Euch zum inneren Ring des Apothekariums. Martha Alliestar ist dort, sie wird Euch in der Kräuterkunde unterrichten.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14913', 'deDE', 'Das Gasthaus - wenn man es so nennen kann - ist am oberen Ring des Handwerksviertels. Die haben nicht mal Betten.', 'Das Gasthaus - wenn man es so nennen kann - ist am oberen Ring des Handwerksviertels. Die haben nicht mal Betten.', 'Norman betreibt das, was er ein Gasthaus nennt, am oberen Ring des Handwerksviertels. Nicht mehr als eine staubige Wandnische ist das.', 'Norman betreibt das, was er ein Gasthaus nennt, am oberen Ring des Handwerksviertels. Nicht mehr als eine staubige Wandnische ist das.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14914', 'deDE', 'Die Stadtschreiberin ist im Apothekarium, jemand namens Margaux Pergament.', 'Die Stadtschreiberin ist im Apothekarium, jemand namens Margaux Pergament.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14915', 'deDE', 'Der Lederer der Stadt befindet sich am inneren Ring des Schurkenviertels.', 'Der Lederer der Stadt befindet sich am inneren Ring des Schurkenviertels.', 'Arthur Moor ist der Lederer. Er kümmert sich drüben im Schurkenviertel um die Lehrlinge, am inneren Ring.', 'Arthur Moor ist der Lederer. Er kümmert sich drüben im Schurkenviertel um die Lehrlinge, am inneren Ring.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14916', 'deDE', 'Wenn Ihr Schlüssel benötigt, sprecht mit Walter Soref im Schurkenviertel. Aber haltet die Augen offen.', 'Wenn Ihr Schlüssel benötigt, sprecht mit Walter Soref im Schurkenviertel. Aber haltet die Augen offen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14917', 'deDE', 'Magier? Sie sind im Magieviertel, im Zentralgebäude. Hätte gedacht, Ihr wärt klug genug um soweit mitzudenken.', 'Magier? Sie sind im Magieviertel, im Zentralgebäude. Hätte gedacht, Ihr wärt klug genug um soweit mitzudenken.', 'Am äußeren Ring des Magieviertels werdet Ihr ein großes Gebäude finden, zu dem Treppen führen. Dort verstecken sich die Magier.', 'Am äußeren Ring des Magieviertels werdet Ihr ein großes Gebäude finden, zu dem Treppen führen. Dort verstecken sich die Magier.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14918', 'deDE', 'Der Briefkasten ist im Handwerksviertel, in der Nähe dieses verstaubten Sarglagers, das sie einen Gasthof nennen.', 'Der Briefkasten ist im Handwerksviertel, in der Nähe dieses verstaubten Sarglagers, das sie einen Gasthof nennen.', 'Briefkasten? Geht zum Gasthof im Zentrum des oberen Stadtrings. Er ist direkt dort.', 'Briefkasten? Geht zum Gasthof im Zentrum des oberen Stadtrings. Er ist direkt dort.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14920', 'deDE', 'Wenn Ihr einen Paladin finden wollt, müsst Ihr zum Königsviertel gehen. Dort ist irgend so ein Elf, der Euch was beibringen kann.', 'Wenn Ihr einen Paladin finden wollt, müsst Ihr zum Königsviertel gehen. Dort ist irgend so ein Elf, der Euch was beibringen kann.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14921', 'deDE', 'Die Priester findet Ihr am äußeren Ring des Kriegsviertels.', 'Die Priester findet Ihr am äußeren Ring des Kriegsviertels.', 'Macht Euch zum Kriegsviertel auf. Die Priester sind dort am äußeren Ring und stellen sicher, dass einzelne Körperteile nicht zu lang lose herumliegen.', 'Macht Euch zum Kriegsviertel auf. Die Priester sind dort am äußeren Ring und stellen sicher, dass einzelne Körperteile nicht zu lang lose herumliegen.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14922', 'deDE', 'Die Schurken sind im Schurkenviertel. So einfach. Haltet am äußeren Ring nach ihnen Ausschau.', 'Die Schurken sind im Schurkenviertel. So einfach. Haltet am äußeren Ring nach ihnen Ausschau.', 'Geht zum äußeren Ring des Schurkenviertels, wenn Ihr Schurken finden wollt.', 'Geht zum äußeren Ring des Schurkenviertels, wenn Ihr Schurken finden wollt.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14923', 'deDE', 'Der Name des Kürschners hier lautet Killian Hagey. Er arbeitet im Schurkenviertel, am inneren Ring.', 'Der Name des Kürschners hier lautet Killian Hagey. Er arbeitet im Schurkenviertel, am inneren Ring.', 'Wenn Ihr lernen möchtet, wie man etwas häutet, geht zum Schurkenviertel. Der Kürschner hat dort am inneren Ring seinen Laden.', 'Wenn Ihr lernen möchtet, wie man etwas häutet, geht zum Schurkenviertel. Der Kürschner hat dort am inneren Ring seinen Laden.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14924', 'deDE', 'Falls Ihr Eure Begleiter wirklich einer Untoten anvertrauen wollt, sprecht mit Anya Maulray beim Gasthaus am oberen Ring des Handwerksviertels.', 'Falls Ihr Eure Begleiter wirklich einer Untoten anvertrauen wollt, sprecht mit Anya Maulray beim Gasthaus am oberen Ring des Handwerksviertels.', 'Ihr könnt mit Anya Maulray beim Gasthaus am oberen Ring des Handwerksviertels sprechen. Ich würde es nicht empfehlen, aber es sind Eure Begleiter.', 'Ihr könnt mit Anya Maulray beim Gasthaus am oberen Ring des Handwerksviertels sprechen. Ich würde es nicht empfehlen, aber es sind Eure Begleiter.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14925', 'deDE', 'Der Schneider hier ist Josef Gregorian. Er befindet sich am inneren Ring des Magieviertels.', 'Der Schneider hier ist Josef Gregorian. Er befindet sich am inneren Ring des Magieviertels.', 'Geht zum inneren Ring des Magieviertels. Dort ist der Schneider.', 'Geht zum inneren Ring des Magieviertels. Dort ist der Schneider.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14926', 'deDE', 'Ihr habt vielleicht Nerven, mich auf Hexenmeister anzusprechen. Geht zum äußeren Ring des Magieviertels und verschwindet aus meinen Augen.', 'Ihr habt vielleicht Nerven, mich auf Hexenmeister anzusprechen. Geht zum äußeren Ring des Magieviertels und verschwindet aus meinen Augen.', 'Ihr findet die Hexenmeister im größten Gebäude des Magieviertels - Aber wehe, wenn es Ärger geben sollte, bekommt Ihr es mit mir zu tun.', 'Ihr findet die Hexenmeister im größten Gebäude des Magieviertels - Aber wehe, wenn es Ärger geben sollte, bekommt Ihr es mit mir zu tun.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14928', 'deDE', 'Wenn Ihr lernen möchtet, wie man mit Waffen umgeht, geht zu Archibald am inneren Ring des Kriegsviertels.', 'Wenn Ihr lernen möchtet, wie man mit Waffen umgeht, geht zu Archibald am inneren Ring des Kriegsviertels.', 'Gut zu wissen, dass Ihr etwas Nützliches lernen wollt. Geht zu Archibald am inneren Ring des Kriegsviertels.', 'Gut zu wissen, dass Ihr etwas Nützliches lernen wollt. Geht zu Archibald am inneren Ring des Kriegsviertels.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14929', 'deDE', 'Ich kann es Euch nicht verdenken, dass Ihr hier wegwollt. Begebt Euch nach oben und geht dann von den Ruinen aus in nördliche Richtung. Die Zeppeline legen von den zwei Türmen dort ab.', 'Ich kann es Euch nicht verdenken, dass Ihr hier wegwollt. Begebt Euch nach oben und geht dann von den Ruinen aus in nördliche Richtung. Die Zeppeline legen von den zwei Türmen dort ab.', 'Die Zeppelintürme befinden sich direkt nördlich von Unterstadt.', 'Die Zeppelintürme befinden sich direkt nördlich von Unterstadt.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14931', 'deDE', 'Überrascht, einen Orc hier zu sehen? Braucht Ihr nicht zu sein.$B$BDie Verlassenen müssen im Auge behalten werden. Hätten wir ihnen von Anfang an mehr Aufmerksamkeit geschenkt, dann wäre es an der Pfo', 'Überrascht, einen Orc hier zu sehen? Braucht Ihr nicht zu sein.$B$BDie Verlassenen müssen im Auge behalten werden. Hätten wir ihnen von Anfang an mehr Aufmerksamkeit geschenkt, dann wäre es an der Pfo', 'Ich hoffe, Ihr seid nicht auf der Suche nach diesen Monstrositäten. Den Apothekern kann man nicht über den Weg trauen und das bedeutet, dass keine ihrer kleinen Konstrukte in den Hallen patrouillieren', 'Ich hoffe, Ihr seid nicht auf der Suche nach diesen Monstrositäten. Den Apothekern kann man nicht über den Weg trauen und das bedeutet, dass keine ihrer kleinen Konstrukte in den Hallen patrouillieren', 'Lok\'tar. Ihr solltet hier besser nichts versuchen, was Misstrauen erregen könnte. Wenn diese Untoten Teil der Horde sein wollen, sollten sie sich unter unserer Überwachung besser vorsehen.', 'Lok\'tar. Ihr solltet hier besser nichts versuchen, was Misstrauen erregen könnte. Wenn diese Untoten Teil der Horde sein wollen, sollten sie sich unter unserer Überwachung besser vorsehen.', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14940', 'deDE', 'Es stimmt, unsere neuen... Gäste wirken etwas einschränkend auf unsere Arbeit, aber nach einem versuchten Völkermord ist es nur natürlich, etwas misstrauisch zu sein. Vielleicht wird es mit der Zeit m', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14984', 'deDE', 'Ich vermute, dieser kalte Empfang sollte mich nicht überraschen. Lange haben wir uns versteckt und die Trennung von unseren Brüdern war notwendig.$B$BDiese Zeit, jedoch, ist nun vorbei. Ob es die Bewo', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15037', 'deDE', 'Es gibt doch tatsächlich welche, die nicht an die legendären Nachtelfirokesen glauben.$B$BDie Trottel tun mir leid.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15040', 'deDE', 'Ein paar Wochen in diesem erbärmlichen Schloss werden uns Zeit geben, an ein paar neuen Produkten zu arbeiten und andere das unschöne... Missverständnis in der Drachenöde vergessen lassen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15066', 'deDE', 'Seid Ihr überrascht, mich hier zu sehen? Jeder braucht frische Wäsche und was ist besser, als den eigenen Wasserelementar dafür zu benutzen?$B$BEinfach die Wäsche bringen, eine symbolische Gebühr zahl', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15074', 'deDE', 'Was führt Euch her, $C?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15076', 'deDE', 'Vertraut niemals den Sonnenhäschern. Sie arbeiten hart daran, uns in den Augen unserer Verbündeten beim Argentumkreuzzug schlecht zu machen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15077', 'deDE', 'Da seid Ihr ja! Ich hatte schon angefangen zu glauben, dass die Sonnenhäscher Euch abgefangen hätten. Seid Ihr bereit, den Folianten unseren Vertretern in Eiskrone zu überbringen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15081', 'deDE', '', 'Ich kann ihn nicht lange aufhalten, wir müssen sofort weg!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15101', 'deDE', 'Die alten Wunden machen sich immer noch bemerkbar, $GJunge:Mädel;. Selbst jetzt noch spüre ich sie. Ja, ich habe noch eine Rechnung mit Arthas offen und Ihr dürft mir glauben, dass ich dem Kerl noch d', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15103', 'deDE', 'Dies ist das letzte Gefecht. Was hier geschieht, wird unvergessen bleiben. Wie immer es ausgeht, man wird wissen, dass wir ehrenvoll kämpften, für die Freiheit und Sicherheit unserer Völker! In diesen', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15119', 'deDE', 'Euer Jormungar kann sich nur unter dem Schnee bewegen, allerdings ist es ihm dann nicht möglich, anzugreifen. Sobald Euer Jormungar auftaucht, kann er seinen mächtigen Säureangriff einsetzen, um die R', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15123', 'deDE', 'Habt Ihr Euer Raketenpack verloren? Habt Ihr mal in Euren Taschen nachgesehen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15134', 'deDE', 'Na, $Geiner:eine:c; $C wie Ihr kommt mir gerade recht! Ich teste eine neue raketengetriebene Flugvorrichtung und brauche Eure Hilfe!$B$BNehmt einfach eins von diesen Geschirren und legt es unter Eurer', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15152', 'deDE', '', 'Ich werde hier warten, bis weitere Verstärkung eintrifft, aber Ihr müsst in Bewegung bleiben. Wir werden nicht viel Zeit haben, bis Arthas merkt, dass wir hier sind.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15155', 'deDE', 'Das scheinen die Überreste von Thalorien Dämmersucher zu sein, dem letzten Träger von Quel\'Delar.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15157', 'deDE', '', 'Ich kann ihn nicht lange aufhalten, wir müssen sofort weg!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15158', 'deDE', 'Macht Euch bereit, $C.$B$BDie Zeit ist gekommen, da die Herrschaft des Lichkönigs zu Ende geht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15159', 'deDE', 'Das Äscherne Verdikt ist eine auserwählte Gruppe der Talentiertesten und Besten der Schwarzen Klinge und des Argentumkreuzzugs. Seine Erschaffung symbolisiert die Einigkeit unserer Truppen, während wi', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15160', 'deDE', 'Sprecht mit Alchemist Finkelstein, $C. Er kann Euch den rechten Weg weisen.$B$BStellt Eure Loyalität unter Beweis und Ihr werdet die Methoden erlernen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15166', 'deDE', 'Der Lichkönig ist nahe, $N. Seine Stimme verhöhnt mich...$B$BBringen wir ihn endlich zum Schweigen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15170', 'deDE', 'Tag! Ich vertrete die Chemiemanufaktur Krone.$B$BSucht Ihr ein Geschenk? Arbeit? Vielleicht eine Kleinigkeit für Euch selbst? Nun, $GFreund:meine Liebe;, hier seid Ihr richtig.$B$BWenn ich nicht habe,', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15171', 'deDE', 'Wir nutzen diese Vorräte, um alle für einen Kampf vorzubereiten.$B$BGeht schon einmal weiter. Wir sehen uns dann später.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15172', 'deDE', 'Ich hole lieber die übrigen Sklaven zusammen und mache sie bereit für den Kampf. Wir holen Euch ein, bevor Ihr auf Geißelfürst Tyrannus trefft.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15174', 'deDE', 'Wir folgen Euch, Ihr Helden. Der Geißelfürst soll noch heute Abend sterben.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15188', 'deDE', 'He, hört mal, ich weiß ja nicht, wer Ihr seid oder warum Ihr mir folgt, aber ich werde demnächst an Bord eines Schiffs gehen und ich will alleine bleiben, kapiert?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15189', 'deDE', '', 'Vergebt mir, Helden. Ich hätte auf Uther hören sollen. Ich... Ich musste es selbst sehen. Ein letztes Mal in seine Augen blicken. Es tut mir leid.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15190', 'deDE', '', 'In Sicherheit … für den Moment. Seine Stärke hat sich verzehnfacht. Nur eine Armee kann den Lichkönig zerstören. Eine Armee, größer, als selbst die Horde sie aufstellen kann.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15191', 'deDE', '', 'Ich werde hier warten, bis weitere Verstärkung eintrifft, aber Ihr müsst in Bewegung bleiben. Arthas wird bald bemerken, dass wir hier sind.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15194', 'deDE', 'Es war ein heftiger Kampf, $N! Ruht Euch einen Moment lang aus und sammelt Eure Kräfte. Wer weiß, was hinter dieser Türe liegt?$B$BLasst mich wissen, wenn Ihr bereit seid, dann werden wir uns auf den ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15195', 'deDE', 'Seid Ihr sicher, $GJunge:Mädel;? Ihr braucht nicht noch eine Minute oder zwei?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15206', 'deDE', '', 'Wir haben keine Zeit zu verlieren, $C. Warum haltet Ihr Euch hier auf?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15207', 'deDE', '', 'Wir haben nicht viel Zeit. Was braucht Ihr?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15209', 'deDE', '<Er schaut zur Rakete und grinst nervös.>$B$BNa schön, gut, dann hab ich eben ein paar Raketen für so ein paar Typen hergestellt. Na und? Das Gold war in Ordnung und ich bin für meine Arbeit bezahlt w', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15210', 'deDE', 'Hört mal, ich weiß es nicht, ja? Keine Ahnung!$B$BIch bin bloß dafür bezahlt worden, Raketen zu machen, die eine chemische Ladung abschießen, das ist alles. Der Auftrag ist erledigt. Sie sind fertig, ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15211', 'deDE', 'Ihr denkt, das hätte ich mir gemerkt? Hört zu. Die haben mir nicht genug gezahlt, um mich jetzt mit Euch abzugeben, und in dem Vertrag stand nur, dass ich den Mund halten soll. Also werde ich einfach ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15212', 'deDE', 'Kennt Ihr schon unser \"Günstige Glücksbringer\"-Angebot?$B$BOffizielle Maskottchen von Veranstaltungen der Manufaktur Krone, so wie ich, sind berechtigt, Sammelsets für hübsche Glücksbringer zu verteil', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15214', 'deDE', 'Was Ihr auch braucht, Brazie Hatz! Ha ha! Na, verstanden?$B$BUnd ich rede nicht nur von \"legalem\" Zeug. EGAL, WAS IHR BRAUCHT! Ich besorg\' Euch alles...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15215', 'deDE', '', 'Der Klinge, die mein Leben beendete, so nahe zu sein... Die Schmerzen... erneuert.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15217', 'deDE', '$GTapferer Soldat:Tapfere Soldatin; der Horde, ich grüße Euch! Ohne Euch und Eure Verbündeten wären wir von dem Kanonenboot der Allianz zerstört worden.$B$BRuht Euch einen Moment aus, bevor wir mit un', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15218', 'deDE', 'Seid Ihr sicher, $N? Wir müssen der Geißel mit ganzer Kraft entgegentreten, wenn wir siegen wollen. Wenn Ihr keine weitere Rast braucht, dann werden wir beginnen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15219', 'deDE', 'Ich habe gehört, dass mein Junge hier sei. Dass er jetzt ein mächtiger Todesritter im Dienst des Lichkönigs sei. Wenn das stimmt, dann muss die Blutlinie von mir gekappt werden.$B$BJetzt besteht meine', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15221', 'deDE', 'Der Transporter schimmert in einem kristallinen Licht.', 'Der Transporter schimmert in einem kristallinen Licht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15240', 'deDE', 'Ich werde Euch in den Sonnenbrunnen begleiten, wenn Ihr soweit seid.', 'Ich werde Euch in den Sonnenbrunnen begleiten, wenn Ihr soweit seid.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15256', 'deDE', 'Diese \'Chemiemanufaktur Krone\' gefällt mir ganz und gar nicht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15282', 'deDE', 'Habt Ihr ein paar Pakete zu verschicken?', 'Habt Ihr ein paar Pakete zu verschicken?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15284', 'deDE', 'Hier stinkt etwas ganz gewaltig und zum allerersten Mal sind es nicht meine Socken...', 'Hier stinkt etwas ganz gewaltig und zum allerersten Mal sind es nicht meine Socken...', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15287', 'deDE', 'Ein wahrhaft faszinierendes Schwert, findet Ihr nicht? Das ist keine von Zwergen geschmiedete Waffe, nein, das hier ist weit älter.$B$BDie Vertreter der Sonnenhäscher und des Silberbunds scheinen ziem', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15290', 'deDE', 'Lange habe ich auf diesen Tag gewartet. Seid Ihr und Eure Verbündeten bereit, den Lichkönig zur Rechenschaft zu ziehen? Auf Euren Befehl stürmen wir los!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15297', 'deDE', 'Eine selbst hinzugefügte Wunde. Ich konnte es nicht ertragen, eine Welt zu sehen, in der ich unter dem Einfluss des Lichkönigs so viele Gräueltaten begangen habe.$B$BEs war mein erster Schritt, meine ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15298', 'deDE', 'Einst war ich ein Paladin, $N. Jetzt meidet das Licht mich, meine Sünden waren einfach zu groß.$B$BIch werde meine Verbündeten nach Kräften unterstützen, aber ich werde mich niemals wieder der Mächte ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15310', 'deDE', 'Endlich, die Verstärkung, auf die ich gewartet habe! Ich habe den Papierkram, den Schnappbolz braucht, schon vorbereitet. Jetzt brauchen wir nur noch jemanden, der das Ganze überstellt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15319', 'deDE', 'Varimathras? Nein, er hat sich seit einiger Zeit nicht mehr gemeldet. Es ist zweifellos mit einem wichtigen Auftrag seiner Königin betraut.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15320', 'deDE', 'Während Varimathras, ähm... fort ist, bin ich damit beauftragt worden, Hauptmann Bragor zu unterstützen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15321', 'deDE', 'Da der Großteil der Streitkräfte Unterstadts zum Hafen der Vergeltung verlegt wurde, hat der Kriegshäuptling uns geschickt, um die Verteidigung der Stadt zu verstärken.', 'Da der Großteil der Streitkräfte Unterstadts zum Hafen der Vergeltung verlegt wurde, hat der Kriegshäuptling uns geschickt, um die Verteidigung der Stadt zu verstärken.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15339', 'deDE', '', 'Frostgram: die Klinge, die unser Königreich zerstörte...', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15340', 'deDE', '', 'Nachdem unsere Agenten unzählige Male versagt haben, bei dem Versuch, Proben der Geißelseuche zu sammeln, mit denen man arbeiten kann, bin ich selbst an die Front gekommen, um die weiteren Bemühungen ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15343', 'deDE', '', 'Das ist unglaublich! Ihr seid bedeckt mit wirksamer, ansteckender, fantastischer Seuche! Bitte, lasst mich diese makellosen Proben nehmen. Ich werde auch jedwede fortbestehende Infektion von Euren Ver', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15379', 'deDE', '', 'Sterbliche, Ihr habt mich vor einem Schicksal bewahrt, das schlimmer ist als der Tod. Wäre ich ihnen überlassen gewesen, wäre es den Zauberern des Lichkönigs irgendwann gelungen, meinen Willen zu brec', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15400', 'deDE', 'Eine scharfsinnige Frage, $C. Schreibern ist es möglich, mächtige Tarotkarten entstehen zu lassen, allerdings haben sie dabei nicht die volle Kontrolle darüber, was sie erschaffen. Das Schicksal entsc', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15432', 'deDE', 'Überall, nur nicht hier...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15445', 'deDE', 'Grüße! Ich stelle Teile von Freyas peitschergewirkter Rüstung für Druiden her und verkaufe sie auch gerne.', 'Grüße! Ich stelle Teile von Freyas peitschergewirkter Rüstung für Druiden her und verkaufe sie auch gerne.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15446', 'deDE', 'Grüße! Ich stelle Teile der Rüstung der Frosthexe für Schamanen her und verkaufe sie auch gerne.', 'Grüße! Ich stelle Teile der Rüstung der Frosthexe für Schamanen her und verkaufe sie auch gerne.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15447', 'deDE', 'Grüße! Ich stelle Teile der Rüstung der Schattenklinge für Schurken her und verkaufe sie auch gerne.', 'Grüße! Ich stelle Teile der Rüstung der Schattenklinge für Schurken her und verkaufe sie auch gerne.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15449', 'deDE', 'Grüße! Ich stelle Teile der Blutjägerrüstung der Ahn\'kahar für Jäger her und verkaufe sie auch gerne.', 'Grüße! Ich stelle Teile der Blutjägerrüstung der Ahn\'kahar für Jäger her und verkaufe sie auch gerne.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15450', 'deDE', 'Grüße! Ich stelle Teile der Rüstung des Ymirjarfürsten für Krieger her und verkaufe sie auch gerne.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15451', 'deDE', 'Grüße! Ich stelle Teile der Rüstung des Lichts für Paladine her und verkaufe sie auch gerne.', 'Grüße! Ich stelle Teile der Rüstung des Lichts für Paladine her und verkaufe sie auch gerne.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15452', 'deDE', 'Grüße! Ich stelle Teile der Rüstung des purpurroten Akolyten für Priester her und verkaufe sie auch gerne.', 'Grüße! Ich stelle Teile der Rüstung des purpurroten Akolyten für Priester her und verkaufe sie auch gerne.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15453', 'deDE', 'Grüße! Ich stelle Teile der Rüstung des Blutmagiers für Magier her und verkaufe sie auch gerne.', 'Grüße! Ich stelle Teile der Rüstung des Blutmagiers für Magier her und verkaufe sie auch gerne.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15454', 'deDE', 'Grüße! Ich stelle Teile der Rüstung des dunklen Zirkels für Hexenmeister her und verkaufe sie auch gerne.', 'Grüße! Ich stelle Teile der Rüstung des dunklen Zirkels für Hexenmeister her und verkaufe sie auch gerne.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15578', 'deDE', 'Endlich ist die Zeit für die Exilgnome gekommen, zurückzukehren.$B$BWir werden Gnomeregan wieder unser Zuhause nennen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15580', 'deDE', 'Die Zwerge haben uns nun schon einige Zeit lang eine Heimat gegeben und sie haben unseren ganzen Dank. Doch nun ist es an der Zeit, der Herrschaft des verräterischen Thermadrahts über unsere Heimat ei', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15581', 'deDE', 'Operation: Gnomeregan ist in verschiedene Phasen unterteilt:$B$BAls erstes müssen wir den Flugabwehrturm einnehmen. Das wird unseren Fliegern erlauben, uns Luftunterstützung zu geben, während wir in G', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15602', 'deDE', 'Das fortwährende Opfer des Äschernen Verdikts und von Helden, wie Ihr es seid, bringt uns alle unserem Sieg näher. Jetzt bin ich zur Eiskronenzitadelle gekommen, um denen zu helfen, die die Geißel bek', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15605', 'deDE', 'Ihr habt Euch entschieden. Ob diese Entscheidung für Euch Leben oder Tod bedeutet, liegt nun bei Euch.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15606', 'deDE', 'Mit der Kriegshymnenoffensive hier in der Eiskronenzitadelle ist der Sieg in greifbare Nähe gerückt! Der Tod des Lichkönigs wird der Horde Ruhm bringen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15607', 'deDE', 'Eine mutige Entscheidung! Ich respektiere Euer Streben nach Ruhm und Ehre! Sterbt glorreich, $N!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15615', 'deDE', 'Sieht kräftig genug aus, um einen Gnom zu tragen. Vielleicht auch zwei.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15704', 'deDE', '', 'Stimmt, ich habe Euch hier schon mal gesehen! Wie konnte ich das nur vergessen? Ich kann Euch jetzt nach Stratholme teleportieren, wenn Ihr wünscht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15779', 'deDE', 'Ich bin nur für den Fall hier, dass jemand in Ohnmacht fällt. Die neuen Rekruten sind ein wenig aufgeregt und übernehmen sich manchmal.$B$B ... na ja. Einerseits das, und andererseits explodiert manch', 'Ich bin nur für den Fall hier, dass jemand in Ohnmacht fällt. Die neuen Rekruten sind ein wenig aufgeregt und übernehmen sich manchmal.$B$B ... na ja. Einerseits das, und andererseits explodiert manch', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15796', 'deDE', '', 'Ich wa\' eine der easten, die Vol\'jins Ruf zu den Waffen folgte. Schließt Iah Euch an?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15806', 'deDE', 'Wenn Ihr \'ne Fledamaus braucht, seid Ihr hier richtich. Ich kümmer mich um all die Fledamäuse von die Späha.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15846', 'deDE', 'Mögen dich die Geister leiten, $C.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15853', 'deDE', 'Ich sage Euch, dieser Coren Düsterbräu ist verrückt geworden.$B$BEs ist doch nicht MEIN Fehler, dass er nicht zum Braufest eingeladen wurde! Aber das hat ihn und seine Dunkeleisenspießgesellen ja nich', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15855', 'deDE', '', 'Der Frostfürst, Ahune, muss aufgehalten werden.$B$BIn eben diesem Moment gewinnt er Gestalt, beschworen aus seinem Elementargefängnis von den wahnsinnigen Kultisten des Schattenhammers.$B$BWenn es ihm', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15858', 'deDE', 'Ihr habt ja wirklich Mumm, $GKumpel:Fräuleinchen;, dass Ihr hier so unangekündigt reinschneit.$B$BWas wollt Ihr?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15859', 'deDE', 'Was?! Was habt Ihr gesagt?! Wie könnt Ihr es wagen?! Mein Bier reicht jeder Brühe, die sie da auf dem Braufest ausschenken, das Wasser reichen!$B$BFür diese Beleidigung werdet Ihr bezahlen, $C!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15863', 'deDE', 'Willkommen in Tüftlerstadt!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15864', 'deDE', 'Dieser Eissplitter lässt das tiefe Rumpeln und Knacken gewaltiger Gletscherzungen erklingen. Seine Kälte lässt Eure Gedanken erstarren und treibt sie in einen leblosen, endlosen Winter.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15865', 'deDE', 'Zalazanes Zeit ist gekommen! Die Echoinseln werden wieder den Dunkelspeeren gehör\'n!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15866', 'deDE', 'Die Dunkelspeere haben wieder ein Zuhause! Un\' wir hätt\'n es nich ohne deine Hilfe geschafft, $N. Jetzt, wir feiern!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15868', 'deDE', '', 'Es ist meine Aufgabe, sicherzustellen, dass der gesamte Angriff präzise wie ein Uhrwerk abläuft.$B$BHochtüftler Mekkadrill ist im Moment draußen im Feld, aber es ist vorgesehen, dass er demnächst noch', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15873', 'deDE', 'Vol\'jin hat mir befohl\'n, die Trommels zu schlagen, bis er wieder da ist. Wenn du auf ihn warten willst, er wird bald wieder da sein, um die Inseln zurückzuerobern.$B$BWarte einfach hier und hör ein b', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15876', 'deDE', 'Wir machen uns bereit, die Echoinseln zurückzuerobern. Zalazane hat jetzt schon ganz schön lange seine Voodoomagie gewirkt und den Leuten erzählt, dass er tot ist, und uralte dunkle Rituale vollzogen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15877', 'deDE', 'Wir brauchen die Hilfe von Verbündeten auf den Inseln.$B$BZen\'tabra hat die Tiere dort schon lange beobachtet. Wir brauchen ihre Hilfe und die Hilfe von den Tieren.$B$BBwonsamdi ist ein mächtiger Loa,', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15906', 'deDE', 'Ihr werdet zwei Draenei im Gespräch mit Tyrande Wisperwind im oberen Geschoss des Tempel des Mondes finden.$B$BSie werden wissen, wie sie Euch helfen können.', 'Ihr werdet zwei Draenei im Gespräch mit Tyrande Wisperwind im oberen Geschoss des Tempel des Mondes finden.$B$BSie werden wissen, wie sie Euch helfen können.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('15921', 'deDE', 'Denen gewidmet, die während des Krieges in den Eisigen Weiten der Geißel zum Opfer fielen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('16777215', 'deDE', 'Seid gegrüßt, $N', 'Seid gegrüßt, $N', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
